INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/72/79/72792fa10d4ca61295194377da0bcc05.mp3', 'n.惭愧，羞耻', '44.7', '6', 4, 'ʃeim', 'They have all been put to shame by a boy who, while playing truant, travelled 1,600 miles.', 'http://static2.iyuba.cn/newconcept/british/2/2_73.mp3', '而有那么一个小男孩，他在逃学期间旅行了1,600英里，从而使上述所有逃学的孩子们都相形见绌了。', '35.9', 2073, 'shame', 0, 0, 2, 1073, 1691);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/fc/d2/fcd260966da70e915cafd6eade1077d0.mp3', 'v.搭便车旅行', '52.7', '7', 5, '''hitʃˌhaik', 'He hitchhiked to Dover and, towards evening, went into a boat to find somewhere to sleep.', 'http://static2.iyuba.cn/newconcept/british/2/2_73.mp3', '他搭便车到了多佛，天快黑时钻进了一条船，想找个地方睡觉。', '46.1', 2073, 'hitchhike', 0, 0, 2, 1073, 1692);
