INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/55/ab/55ab85e676ef2f7305f22d8119d53410.mp3', 'n.卡车', '71.8', '10', 7, 'ˈlɒri', 'From there, he hitchhiked to Paris in a lorry.', 'http://static2.iyuba.cn/newconcept/british/2/2_73.mp3', '从那里他又搭上卡车到了巴黎。', '67.7', 2073, 'lorry', 0, 0, 2, 1073, 1694);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/2d/e4/2de42fbb00305f8282310f1b3e10ce9a.mp3', 'n.边界', '91.0', '12', 8, 'ˈbɔ:də (r)', 'The next car the boy stopped did not take him into the centre of Paris as he hoped it would, but to Perpignan on the French-Spanish border.', 'http://static2.iyuba.cn/newconcept/british/2/2_73.mp3', '男孩截住的下一辆车，没有像他希望的那样把他带到巴黎市中心，而是把他带到了法国和西班牙边界上的佩皮尼昂。', '80.6', 2073, 'border', 0, 0, 2, 1073, 1695);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/13/7e/137e516ff7c0d86dd62510cce8f6e2e2.mp3', 'v.逃避，逃离', '105.7', '14', 9, 'iˈveid', 'He has surely set up a record for the thousands of children who dream of evading school.', 'http://static2.iyuba.cn/newconcept/british/2/2_73.mp3', '他无疑为成千上万梦想逃避上学的孩子们创造了一项纪录。', '99.9', 2073, 'evade', 0, 0, 2, 1073, 1696);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/84/9e/849efc4c03b9c7c88e61cf5b0b13b734.mp3', 'n.舞台灯光', '5.5', '1', 1, 'ˈlaɪmˌlaɪt', '--- Lesson 74 Out of the limelight', 'http://static2.iyuba.cn/newconcept/british/2/2_74.mp3', '舞台之外', '1.5', 2074, 'limelight', 0, 0, 2, 1074, 1697);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/8f/36/8f3613e726dd9b16b466dbfa18ca8be8.mp3', 'n.预防措施', '36.0', '5', 2, 'pri''kɔ:ʃn', 'Dressed in dark glasses and old clothes, they had taken special precautions so that no one should recognize them.', 'http://static2.iyuba.cn/newconcept/british/2/2_74.mp3', '他们戴着墨镜，穿着旧衣裳，特别小心以防别人认出他们。', '28.0', 2074, 'precaution', 0, 0, 2, 1074, 1698);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/50/bd/50bd8c21bfafa6e4e962f6a948b1ef92.mp3', 'n.狂热者，迷', '61.6', '9', 3, 'fæn', 'No newspaper men, no film fans! Why don''t we come more often?', 'http://static2.iyuba.cn/newconcept/british/2/2_74.mp3', '“没有记者，没有影迷！我们为什么不经常来这里呢？”', '56.8', 2074, 'fan', 0, 0, 2, 1074, 1699);
