INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/af/3b/af3b39c56df7a42bdd1644db7a4c057d.mp3', 'adj.欣喜的', '100.1', '14', 12, 'dɪˈlaɪtɪd', 'My wife was delighted that things had returned to normal once more.', 'http://static2.iyuba.cn/newconcept/british/2/2_78.mp3', '一切又都恢复了正常，为此我妻子十分高兴。', '95.8', 2078, 'delighted', 0, 0, 2, 1078, 1750);
