INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/74/e0/74e04ddb55ce3825f65ebec374ef8f0d.mp3', 'n. 衣领', '36.0', '5', 2, 'ˈkɒlə(r)', 'People who work in offices are frequently referred to as ''white-collar workers'' for the simple reason that they usually wear a collar and tie to go to work.', 'http://static2.iyuba.cn/newconcept/british/3/3_4.mp3', '坐办公室的之所以常常被称作“白领工人”，就是因为他们通常是穿着硬领白衬衫，系着领带去上班。', '26.5', 3004, 'collar', 0, 0, 3, 2004, 1959);
