INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/7e/18/7e1813d1263dfab2366123b8928656e3.mp3', 'n. 硬纸板', '83.7', '13', 9, 'ˈkɑːdbɔːd', 'Mrs. Ramsay was instructed to place the money in a cardboard box and to leave it outside her door.', 'http://static2.iyuba.cn/newconcept/british/3/3_19.mp3', '他让拉姆齐太太把钱放在一个纸盒里，然后将纸盒放在门口。', '77.4', 3019, 'cardboard', 0, 0, 3, 2019, 2166);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/1c/ce/1cceb14f9f6fae5f3604e299f49237d9.mp3', 'v. （从银行）取钱', '101.3', '16', 10, 'wɪðˈdrɔ:', 'She withdrew $1000 from her bank and followed the kidnapper''s instructions.', 'http://static2.iyuba.cn/newconcept/british/3/3_19.mp3', '她从银行取出1,000 英镑，并照绑架者的要求做了。', '96.1', 3019, 'withdraw', 0, 0, 3, 2019, 2167);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/9/d/9/9d9c1c358db9f79e631b26e4b768502d.mp3', 'adv. 准时地', '114.8', '18', 11, 'ˈpʌŋktʃʊəlɪ', 'Sure enough, Rastus arrived punctually at seven o''clock that evening.', 'http://static2.iyuba.cn/newconcept/british/3/3_19.mp3', '果然，当天晚上7点正，拉斯特斯准时回来了。', '110.5', 3019, 'punctually', 0, 0, 3, 2019, 2168);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/c6/78/c678e636ae895f18ac7f3651edde2ced.mp3', 'v. 使吃惊', '126.1', '20', 12, 'ə''staʊnd', 'The police were astounded when Mrs. Ramsay told them what she had done.', 'http://static2.iyuba.cn/newconcept/british/3/3_19.mp3', '拉姆齐太太把她所做的事告诉了警察，警察听后大为吃惊。', '122.3', 3019, 'astound', 0, 0, 3, 2019, 2169);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/b3/9e/b39ecd658b2b74a2712303acf766b4a7.mp3', 'n. 先驱', '4.3', '1', 1, 'ˌpaɪə ˈnɪə(r)', '--- lesson 20 Pioneer pilots', 'http://static2.iyuba.cn/newconcept/british/3/3_20.mp3', '飞行员的先驱', '1.5', 3020, 'pioneer', 0, 0, 3, 2020, 2170);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/dd/26/dd26ea8b2bc48d1b4038b99e4fdf612a.mp3', 'n. 对（英国）贵族的尊称；勋爵', '27.2', '4', 2, 'lɔ:d', 'In 1908 Lord Northcliffe offered a prize of $1,000 to the first man who would fly across the English Channel.', 'http://static2.iyuba.cn/newconcept/british/3/3_20.mp3', '1908年，诺斯克利夫勋爵拿出1,000英镑，作为对第一个飞越英吉利海峡的人的奖励。', '18.3', 3020, 'lord', 0, 0, 3, 2020, 2171);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/be/d6/bed6f45edd28b8af0d876650a2f1df7f.mp3', 'n. 加来（法国港市）', '62.7', '9', 3, 'ˈkæleɪ', 'Two days later, Louis Bleriot arrived near Calais with a plane called ''No. XI''.', 'http://static2.iyuba.cn/newconcept/british/3/3_20.mp3', '两天之后，路易斯.布莱里奥驾驶一名为“11号”的飞机来到加来附近。', '56.7', 3020, 'Calais', 0, 0, 3, 2020, 2172);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/27/3e/273ed54e3e0976767419f78a1864da9d.mp3', 'adj. 陆上的', '77.3', '11', 4, '''əʊvə ˌlænd', 'A week before, he had completed a successful overland flight during which he covered twenty-six miles.', 'http://static2.iyuba.cn/newconcept/british/3/3_20.mp3', '一周以前，他曾成功地进行了一次26英里的陆上飞行。', '70.7', 3020, 'overland', 0, 0, 3, 2020, 2173);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/46/84/4684c60ddc71bc393a1bf007f5452372.mp3', 'n. 拳击', '24.8', '4', 1, 'ˈbɔksɪŋ', 'Boxing matches were very popular in England two hundred years ago.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '两百年前，拳击比赛在英国非常盛行。', '20.8', 3021, 'Boxing', 0, 0, 3, 2021, 2174);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/0a/ac/0aac16330f9a907665ec2fad88f2e8dc.mp3', 'n. 拳击手', '30.0', '5', 2, 'ˈbɒksə(r)', 'In those days, boxers fought with bare fists for prize money.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '当时，拳击手们不戴手套，为争夺奖金而搏斗。', '24.8', 3021, 'boxer', 0, 0, 3, 2021, 2175);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/86/04/8604aecae9ee64a74c52d4c1965bb0ef.mp3', 'adj. 赤裸的', '30.0', '5', 3, 'beə(r)', 'In those days, boxers fought with bare fists for prize money.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '当时，拳击手们不戴手套，为争夺奖金而搏斗。', '24.8', 3021, 'bare', 0, 0, 3, 2021, 2176);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/77/eb/77ebae3f307010839d45f2d1a4763099.mp3', 'n. 职业拳击手（尤指古时赤手拳击手）', '34.3', '6', 4, 'ˈpraɪzfaɪtə(r)', 'Because of this, they were known as ''prizefighters''.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '因此，他们被称作“职业拳击手”。', '30.9', 3021, 'prizefighter', 0, 0, 3, 2021, 2177);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/bd/f8/bdf8eda1b1f0c2095a2ac4c7d60d8378.mp3', 'adj. 粗野的', '44.0', '7', 5, 'krʊ:d', 'However, boxing was very crude, for there were no rules and a prizefighter could be seriously injured or even killed during a match.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '不过，拳击是十分野蛮的，因为当时没有任何比赛规则，职业拳击手有可能在比赛中受重伤，甚至丧命。', '35.0', 3021, 'crude', 0, 0, 3, 2021, 2178);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/9e/e3/9ee383b6961ed99f8ae4fbb4e2d72dc2.mp3', 'n. 侯爵', '62.5', '9', 6, 'ˈmɑ:kwɪs', 'The use of gloves was not introduced until 1860, when the Marquis of Queensberry drew up the first set of rules.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '1860年昆斯伯里侯爵第一次为拳击比赛制定了规则，拳击比赛这才用上了手套。', '54.1', 3021, 'marquis', 0, 0, 3, 2021, 2179);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/7/3/f/73f38794ec06167c970458212ad62092.mp3', 'adv. 严格根据法律意义地', '73.7', '10', 7, 'ˈteknɪkəlɪ', 'Though he was technically a prizefighter, Mendoza did much to change crude prizefighting into a sport, for he brought science to the game.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '虽然门多萨严格来讲不过是个职业拳击手，但在把这种粗野的拳击变成一种体育运动方面，他作出了重大贡献。是他把科学引进了这项运动。', '63.5', 3021, 'technically', 0, 0, 3, 2021, 2180);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/fb/5c/fb5c7f9bb4b32ce2f3bff4662f1ab27b.mp3', 'n. 科学', '73.7', '10', 8, 'ˈsaɪəns', 'Though he was technically a prizefighter, Mendoza did much to change crude prizefighting into a sport, for he brought science to the game.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '虽然门多萨严格来讲不过是个职业拳击手，但在把这种粗野的拳击变成一种体育运动方面，他作出了重大贡献。是他把科学引进了这项运动。', '63.5', 3021, 'science', 0, 0, 3, 2021, 2181);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/db/2b/db2b7a7c6873b2d8779af20aef1910ad.mp3', 'n. 名望', '82.0', '11', 9, 'ˌpɒpjʊ''lærɪti', 'In his day, Mendoza enjoyed tremendous popularity. He was adored by rich and poor alike.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '门多萨在他的全盛时期深受大家欢迎，无论是富人还是穷人都对他祟拜备至。', '74.7', 3021, 'popularity', 0, 0, 3, 2021, 2182);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/33/33/3333c68f2910d11d85c10a82dc6c0989.mp3', 'v. 崇拜，爱戴', '82.0', '11', 10, 'ə ˈdɔː(r)', 'In his day, Mendoza enjoyed tremendous popularity. He was adored by rich and poor alike.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '门多萨在他的全盛时期深受大家欢迎，无论是富人还是穷人都对他祟拜备至。', '74.7', 3021, 'adore', 0, 0, 3, 2021, 2183);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/48/bb/48bb082d4244476f2ef109e51f8f7554.mp3', 'adv. 一样地', '82.0', '11', 11, 'ə ˈlaɪk', 'In his day, Mendoza enjoyed tremendous popularity. He was adored by rich and poor alike.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '门多萨在他的全盛时期深受大家欢迎，无论是富人还是穷人都对他祟拜备至。', '74.7', 3021, 'alike', 0, 0, 3, 2021, 2184);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/f8/6f/f86f4786f77a482d600ad9366292cff4.mp3', 'n. 名声', '88.8', '12', 12, 'feɪm', 'Mendoza rose to fame swiftly after a boxing match when he was only fourteen years old.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '门多萨在14岁时参加了一场拳击赛后一举成名。', '83.2', 3021, 'fame', 0, 0, 3, 2021, 2185);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/da/63/da63a4ac88668e9f0e76f835ccaa4486.mp3', 'adj. 著名的，杰出的', '95.6', '13', 13, 'ˈemɪnənt', 'This attracted the attention of Richard Humphries who was then the most eminent boxer in England.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '这引起了当时英国拳坛名将理查德.汉弗莱斯的注意。', '89.6', 3021, 'eminent', 0, 0, 3, 2021, 2186);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/1a/62/1a6249e39b76e40f48be4e52081af588.mp3', 'adv. 厉害地', '113.2', '16', 14, 'ˈbɪtəlɪ', 'The two men quarrelled bitterly and it was clear that the argument could only be settled by a fight.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '两个人争吵不休，显而易见，只有较量一番才能解决问题。', '106.3', 3021, 'bitterly', 0, 0, 3, 2021, 2187);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/bb/e9/bbe991a1b594ed7d73b4a0f43c407745.mp3', 'v. 打赌', '124.5', '18', 15, 'bet', 'The public bet a great deal of money on Mendoza, but he was defeated.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '公众把大笔赌注下到了门多萨身上，但他却输了。', '119.5', 3021, 'bet', 0, 0, 3, 2021, 2188);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/d5/a9/d5a9c70352da04497dc3a67f2798ba4c.mp3', 'n. 专业学校', '146.4', '21', 16, 'ə''kædəmɪ', 'Meanwhile, he founded a highly successful Academy and even Lord Byron became one of his pupils.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '同时，他建立了一所拳击学校，办得很成功，连拜伦勋爵也成了他的学生。', '139.8', 3021, 'academy', 0, 0, 3, 2021, 2189);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/61/3b/613b277b8e435646a7a48cc002f9c6bb.mp3', 'adj. 浪费的，奢移的', '158.7', '23', 17, 'ɪks ˈtrævɪɡənt', 'Despite this, he was so extravagant that he was always in debt.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '尽管收入不少，但他挥霍无度，经常债台高筑。', '154.5', 3021, 'extravagant', 0, 0, 3, 2021, 2190);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/d8/ea/d8eae8212e2ed885c71f4117e0c39374.mp3', 'n. 贫困', '178.1', '25', 18, 'ˈpɒvəti', 'He was sent to prison for failing to pay his debts and died in poverty in 1836.', 'http://static2.iyuba.cn/newconcept/british/3/3_21.mp3', '他因无力还债而被捕入狱，最后于1836年在贫困中死去。', '165.2', 3021, 'poverty', 0, 0, 3, 2021, 2191);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/ab/b3/abb3cfdd4a9f6a02b15e394a4495e209.mp3', 'v. （戏剧，电影等）连演，连映', '24.1', '4', 1, 'rʌn', 'Some plays are so successful that they run for years on end.', 'http://static2.iyuba.cn/newconcept/british/3/3_22.mp3', '有些剧目十分成功，以致连续上演好几年。', '20.0', 3022, 'run', 0, 0, 3, 2022, 2192);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/9/8/0/980da98409d058c365664ff7ea33dd6b.mp3', 'n. （剧本中的）台词', '33.1', '5', 2, 'laɪnz', 'In many ways, this is unfortunate for the poor actors who are required to go on repeating the same lines night after night.', 'http://static2.iyuba.cn/newconcept/british/3/3_22.mp3', '这样一来，可怜的演员们可倒霉了。因为他们需要一夜连着一夜地重复同样的台词。', '25.1', 3022, 'lines', 0, 0, 3, 2022, 2193);
