INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/da/9c/da9c3748877e7cc063dea8073b33986d.mp3', 'n. 后果，结果', '110.1', '19', 10, 'ˈkɔnsɪkwəns', 'He has deliberately chosen to lead the life he leads and is fully aware of the consequences.', 'http://static2.iyuba.cn/newconcept/british/3/3_27.mp3', '他是故意地选择过那种生活的，并完全清楚以这种方式生活的后果。', '102.5', 3027, 'consequence', 0, 0, 3, 2027, 2286);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/01/67/0167ea1394f0e3c08148fdafe18f5a9e.mp3', 'v. 使苦恼，折磨', '119.8', '20', 11, 'ə''flɪkt', 'He may never be sure where the next meal is coming from, but he is free from the thousands of anxieties which afflict other people.', 'http://static2.iyuba.cn/newconcept/british/3/3_27.mp3', '他可能从不知道下顿饭有无着落，但他不像有人那样被千万桩愁事所折磨。', '110.9', 3027, 'afflict', 0, 0, 3, 2027, 2287);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/7c/c2/7cc260957727a2a3e99ce612807f4f3c.mp3', 'n. 容易', '126.3', '21', 12, 'ɪ:z', 'His few material possessions make it possible for him to move from place to place with ease.', 'http://static2.iyuba.cn/newconcept/british/3/3_27.mp3', '他几乎没有什么财产，这使他能够轻松自如地在各地奔波。', '120.9', 3027, 'ease', 0, 0, 3, 2027, 2288);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/40/5a/405aaff66082ffe7231d7c1f79926c17.mp3', 'n. 大自然', '133.7', '22', 13, 'ˈneɪtʃə(r)', 'By having to sleep in the open, he gets far closer to the world of nature than most of us ever do.', 'http://static2.iyuba.cn/newconcept/british/3/3_27.mp3', '由于被迫在露天睡觉，他比我们中许多人都离大自然近得多。', '127.7', 3027, 'nature', 0, 0, 3, 2027, 2289);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/6a/d8/6ad8d80290670a6a3bea45e228382dcf.mp3', 'n. 蔑视的', '153.4', '25', 14, 'kən ˈtempt', 'We often speak of tramps with contempt and put them in the same class as beggars,', 'http://static2.iyuba.cn/newconcept/british/3/3_27.mp3', '说起流浪汉，我们常常带有轻蔑并把他们与乞丐归为一类。', '147.9', 3027, 'contempt', 0, 0, 3, 2027, 2290);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/ec/f5/ecf52eb242946360d6a421f2742a1de6.mp3', 'adj. 嫉妒的    ', '165.6', '26', 15, '''envɪəs', 'but how many of us can honestly say that we have not felt a little envious of their simple way of life and their freedom from care?', 'http://static2.iyuba.cn/newconcept/british/3/3_27.mp3', '但是，我们中有多少人能够坦率地说我们对流浪汉的简朴生活与无忧无虑的境况不感到有些羡慕呢?', '154.2', 3027, 'envious', 0, 0, 3, 2027, 2291);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/9/7/2/972cdfe47890ae2d8b82dd2f7d366c57.mp3', 'n. 货物，商品', '23.9', '4', 1, 'weəz', 'Small boats loaded with wares sped to the great liner as she was entering the harbour.', 'http://static2.iyuba.cn/newconcept/british/3/3_28.mp3', '当一艘大型班船进港的时候，许多小船载着各种杂货快速向客轮驶来。', '18.1', 3028, 'wares', 0, 0, 3, 2028, 2292);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/47/ae/47ae9ec4c0978a1293d1030e30034b8a.mp3', 'v. 停航下锚', '29.3', '5', 2, 'ˈæŋkə(r)', 'Before she had anchored, the men from the boats had climbed on board', 'http://static2.iyuba.cn/newconcept/british/3/3_28.mp3', '大船还未下锚。小船上的人就纷纷爬上客轮。', '24.9', 3028, 'anchor', 0, 0, 3, 2028, 2293);
