INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/67/d9/67d9d658957273b3e083949d952da78f.mp3', 'v. 纠缠', '59.7', '10', 8, 'ə ˈseɪl', 'I had no sooner got off the ship than I was assailed by a man who wanted to sell me a diamond ring.', 'http://static2.iyuba.cn/newconcept/british/3/3_28.mp3', '我刚下船，就被一个人截住，他向我兜售一枚钻石戒指。', '52.2', 3028, 'assail', 0, 0, 3, 2028, 2299);
