INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/b3/7d/b37d36ca849febe0e250eb05de938495.mp3', 'v. 刻写，雕', '105.4', '19', 10, 'ɪn''skraɪb', 'At the base of the gold cap, the words ''made in the U.S.A'' had been neatly inscribed.', 'http://static2.iyuba.cn/newconcept/british/3/3_28.mp3', '金笔帽下方整齐地刻有“美国制造”字样。', '99.1', 3028, 'inscribe', 0, 0, 3, 2028, 2301);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/ff/ea/ffead8f75bff3df51f1a81bcc8e15945.mp3', 'n. 好处，优惠', '113.8', '20', 11, 'ˈfeɪvə(r)', 'The man said that the pen was worth $50, but as a special favour, he would let me have it for $30.', 'http://static2.iyuba.cn/newconcept/british/3/3_28.mp3', '那人说那支笔值50英镑，作为特别优惠，他愿意让我出30英镑成交。', '106.2', 3028, 'favour', 0, 0, 3, 2028, 2302);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/90/b3/90b37c89da41f432283b1891ce28935c.mp3', 'v. （讲话时）打手势', '131.4', '22', 12, 'dʒe ˈstɪkjə ˌleɪt', 'Gesticulating wildly, the man acted as if he found my offer outrageous, but he eventually reduced the price to $10.', 'http://static2.iyuba.cn/newconcept/british/3/3_28.mp3', '那人激动地打着手势，仿佛我的出价使他不能容忍。但他终于把价钱降到了10英镑。', '122.7', 3028, 'gesticulate', 0, 0, 3, 2028, 2303);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/25/c6/25c6be1ba01eb3f47f8aa9c9c6af3b32.mp3', 'adj. 出人预料的；令人不悦的', '131.4', '22', 13, 'aʊt ˈreɪdʒəs', 'Gesticulating wildly, the man acted as if he found my offer outrageous, but he eventually reduced the price to $10.', 'http://static2.iyuba.cn/newconcept/british/3/3_28.mp3', '那人激动地打着手势，仿佛我的出价使他不能容忍。但他终于把价钱降到了10英镑。', '122.7', 3028, 'outrageous', 0, 0, 3, 2028, 2304);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/96/36/9636a6dbd711697cea8a56ce9b4fa986.mp3', 'v. 硬塞给', '140.6', '23', 14, 'θrʌst', 'Shrugging my shoulders, I began to walk away when, a moment later, he ran after me and thrust the pen into my hands.', 'http://static2.iyuba.cn/newconcept/british/3/3_28.mp3', '我耸耸肩膀掉头走开了。一会儿，他突然从后追了上来，把笔塞到我手里。', '132.5', 3028, 'thrust', 0, 0, 3, 2028, 2305);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/b7/46/b7465ad9caf96c65273dfaa4bfb691e4.mp3', 'adv. 在很大程度上', '22.7', '4', 1, 'ˈlɑ:dʒlɪ', 'Whether we find a joke funny or not largely depends on where we have been brought up.', 'http://static2.iyuba.cn/newconcept/british/3/3_29.mp3', '我们觉得一则笑话是否好笑，很大程度取决于我们是在哪儿长大的。', '17.2', 3029, 'largely', 0, 0, 3, 2029, 2306);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/1d/51/1d51cd4fc605dd5324f91ea1bafb05bb.mp3', 'adj. 喜剧的，可笑的', '46.0', '8', 2, 'ˈkɒmɪk', 'Most funny stories are based on comic situations.', 'http://static2.iyuba.cn/newconcept/british/3/3_29.mp3', '大部分令人发笑的故事都是根据喜剧情节编写的。', '42.7', 3029, 'comic', 0, 0, 3, 2029, 2307);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/31/74/31745b145829050074ad32503cd48006.mp3', 'adj. 普通的', '52.5', '9', 3, 'ˌjuːnɪ ˈvɜːsl', 'In spite of national differences, certain funny situations have a universal appeal.', 'http://static2.iyuba.cn/newconcept/british/3/3_29.mp3', '尽管民族不同，有些滑稽情节却能产生普遍的效果。', '46.8', 3029, 'universal', 0, 0, 3, 2029, 2308);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/16/61/1661a33dd040efd0915e99822c150a2d.mp3', 'n. 滑稽演员，喜剧演员', '77.0', '13', 4, 'kə''mɪ:dɪən', 'Comedians base their jokes on tragic situations like violent death or serious accidents.', 'http://static2.iyuba.cn/newconcept/british/3/3_29.mp3', '喜剧演员根据悲剧情节诸如暴死，重大事故等来编造笑话。', '70.4', 3029, 'comedian', 0, 0, 3, 2029, 2309);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/b8/9f/b89f3f2d2223844c8e8013c8c45d14ea.mp3', 'adj. 讨厌的', '84.5', '14', 5, 'dɪs''teɪstfəl', 'Many people find this sort of joke distasteful. The following example of ''sick humour'' will enable you to judge for yourself.', 'http://static2.iyuba.cn/newconcept/british/3/3_29.mp3', '许多人认为这种笑话是低级庸俗的。下面是个“病态幽默”的实例，你可以据此自己作出判断。', '77.5', 3029, 'distasteful', 0, 0, 3, 2029, 2310);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/7b/f1/7bf1d33b8f065f47de1a36361020187b.mp3', 'v. 一再要求，纠缠', '98.5', '16', 6, 'ˈpestə(r)', 'From the moment he arrived there, he kept on pestering his doctor to tell him when he would be able to go home.', 'http://static2.iyuba.cn/newconcept/british/3/3_29.mp3', '从他进医院那一刻时，他就缠住医生，让医生告诉他什么时候能回家。', '92.4', 3029, 'pester', 0, 0, 3, 2029, 2311);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/46/5d/465d3b154276edf13b024baf58fef3fc.mp3', 'v. 惧怕', '101.8', '17', 7, 'dred', 'He dreaded having to spend Christmas in hospital.', 'http://static2.iyuba.cn/newconcept/british/3/3_29.mp3', '他十分害怕在医院过圣诞。', '98.5', 3029, 'dread', 0, 0, 3, 2029, 2312);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/7d/2b/7d2bd8a6de9b35a0f6318fd4137cffbf.mp3', 'n. 康复', '106.6', '18', 8, 'rɪ''kʌvərɪ', 'Though the doctors did his best, the patient''s recovery was slow.', 'http://static2.iyuba.cn/newconcept/british/3/3_29.mp3', '尽管医生竭力医治，但病人恢复缓慢。', '101.8', 3029, 'recovery', 0, 0, 3, 2029, 2313);
