INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/f7/a6/f7a657c78a15abb5302fcf8acfcfe148.mp3', 'v. 缩进，隐藏', '201.3', '32', 22, 'dɪ ˈsend', 'Few things could be more impressive than the peace that descends on deserted city streets at weekends', 'http://static2.iyuba.cn/newconcept/british/3/3_41.mp3', '周末，空旷的街市笼罩着一种宁静的气氛，没有什么能比此时的宁静更令人难忘了。', '195.1', 3041, 'descend', 0, 0, 3, 2041, 2549);
