INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/2a/f8/2af832533704a04470cdec71908cf9e6.mp3', 'adj. 不好客的', '28.5', '5', 2, '‘hɒ ˈspɪtəbl', 'We always assume that villagers in remote places are friendly and hospitable.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '人们总是以为偏僻的地方的村民们热情好客。', '23.4', 3048, 'hospitable', 0, 0, 3, 2048, 2709);
