INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/0f/12/0f12c3d1e8de40dadef6dd1a69a48dff.mp3', 'n. 周围,近邻', '54.3', '8', 4, 'və ˈsɪnəti', 'We had spent several days in a small town and visited a number of old churches in the vicinity.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '我们在一座小镇上逗留了几天，参观了附近的许多古老的教堂。', '47.8', 3048, 'vicinity', 0, 0, 3, 2048, 2711);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/1c/f2/1cf218adfda3ae9fe7f80e1eadf55a86.mp3', 'adj. 建筑的', '61.0', '9', 5, 'ˌɑ:kɪ ˈtektʃərəl', 'These attracted many visitors, for they were not only of great architectural interest,', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '这些教堂吸引了大量游客，不仅是因为建筑风格奇特，', '55.7', 3048, 'architectural', 0, 0, 3, 2048, 2712);
