INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/4/a/4/4a4f26e49d932ba5fa8c0df658f6afd5.mp3', 'adv. 突然地,意外地', '95.4', '14', 7, 'ə''brʌptlɪ', 'We expected the path to end abruptly, but we found that it traced its way through the trees.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '我们原以为小路会到此突然终止，没想到它到树林中继续向前延伸。', '89.6', 3048, 'abruptly', 0, 0, 3, 2048, 2714);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/53/22/5322239adecab388474673aa3704cc24.mp3', 'v. 徒步行进', '101.0', '15', 8, 'træmp', 'We tramped through the wood for over two hours until we arrived at a deep stream.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '我们在树林中跋涉了两个多小时，到了一条深溪边。', '96.2', 3048, 'tramp', 0, 0, 3, 2048, 2715);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/99/55/99557f46ccef290d9d93c546f64fb7d6.mp3', 'v. （用绳、链、锚）系（船）', '113.0', '17', 9, 'mɔː(r)', 'Suddenly my wife spotted a boat moored to the bank.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '突然，妻子发现岸边泊着一条小船，', '109.4', 3048, 'moor', 0, 0, 3, 2048, 2716);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/46/17/46171b077997b166bb30cf5494eff2f8.mp3', 'v. （用渡船）运', '121.0', '19', 10, 'ˈferɪ', 'We gently woke him up and asked him to ferry us to the other side.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '我们轻轻地把他唤醒，请他把我们摆渡过溪。', '116.8', 3048, 'ferry', 0, 0, 3, 2048, 2717);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/6b/47/6b4722d2b83a086e5b3840e5550e02d6.mp3', 'v. 蔓延，散乱分布', '140.1', '22', 11, 'ˈstræɡl', 'The place consisted of a straggling unmade road which was lined on either side by small houses.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '这儿有一条未经修筑的弯弯曲曲的道路，路两边排列着一些矮小的农舍。', '133.7', 3048, 'straggle', 0, 0, 3, 2048, 2718);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/4d/ce/4dcec4c44874f94793dd30a41534a52f.mp3', 'n. 沙丁鱼，沙丁鱼罐头', '169.1', '25', 12, 'sɑ:''dɪ:n', 'Sitting down on a dilapidated wooden fence near the field, we opened a couple of tins of sardines and had a picnic lunch.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '我们在田边一堵东倒西歪的篱笆墙上坐下来，打开几听沙丁鱼罐头，吃了一顿野外午餐。', '160.8', 3048, 'sardine', 0, 0, 3, 2048, 2719);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/75/42/7542dc7e912162d7e7981176ccb41bbd.mp3', 'n. 破烂衣服', '182.8', '27', 13, 'ræɡ', 'Looking up I saw that we were surrounded by children in rags who were looking at us silently as we ate.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '我抬头一看，发现我们被一群衣衫褴褛的小孩团团围住了，他们在默不作声地看着我们吃饭。', '175.7', 3048, 'rag', 0, 0, 3, 2048, 2720);
