INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/e8/45/e84532c218105cb4675b625af206a4bc.mp3', 'v. 加快', '238.4', '38', 17, 'ˈkwɪkən', 'Turning back down the main street, we quickened our pace and made our way rapidly towards the stream where we hoped the boatman was waiting.', 'http://static2.iyuba.cn/newconcept/british/3/3_48.mp3', '便掉转身子，沿着那条主要街道加快步伐，快速地朝深溪边走去，希望船夫还在那儿等着我们。', '229.4', 3048, 'quicken', 0, 0, 3, 2048, 2724);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/30/f6/30f6bfbf51cf41bbb6d9a748c70f2d5c.mp3', 'adj. 杂乱无章的', '40.4', '7', 1, 'ˈræmblɪŋ', 'She had a huge, rambling country house called ''The Gables''.', 'http://static2.iyuba.cn/newconcept/british/3/3_49.mp3', '她在乡下有一幢巨大杂乱的房子，叫作“山墙庄园”。', '35.9', 3049, 'rambling', 0, 0, 3, 2049, 2725);
