INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/c6/46/c6468c6e8dff1055cd2d567a029cfdd5.mp3', 'n. （瑞典和英国的）司法特派员', '87.6', '10', 10, 'ˈɒmbʊdzmən', 'The official title of the person is ''Justiteombudsmanbut the Swedes commonly refer to him as the ''J.O.'' or ''Ombudsman''.', 'http://static2.iyuba.cn/newconcept/british/3/3_53.mp3', '此人官衔为“司法特派员”，但瑞典人一般管他叫“J.O.”，即“司法特派员”。', '77.2', 3053, 'ombudsman', 0, 0, 3, 2053, 2821);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/46/c4/46c40f747db6347ceb51e8f42a2ce1c5.mp3', 'adj. 保密的', '119.9', '15', 11, 'ˈsiːkrətɪv', 'There is nothing secretive about the Ombudsman''s work, for his correspondence is open to public inspection.', 'http://static2.iyuba.cn/newconcept/british/3/3_53.mp3', '司法特派员的工作没有什么秘密可言，他的信件是公开的，供公众监督。', '113.1', 3053, 'secretive', 0, 0, 3, 2053, 2822);
