INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/56/67/5667da2ff3545d244af39297559ee21e.mp3', 'v. 改变', '137.1', '18', 13, 'ˈɔːltə(r)', 'He may gently reprimand an official or even suggest to parliament that a law be altered.', 'http://static2.iyuba.cn/newconcept/british/3/3_53.mp3', '他可以善意地批评某位官员，也可以甚至向议会提议修改某项法律。', '131.1', 3053, 'alter', 0, 0, 3, 2053, 2824);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/7c/f7/7cf763a6899036590ba6cf85f9927bfa.mp3', 'n. 谴责，指控', '169.3', '23', 14, 'ˌækju ˈzeɪʃn', 'There was nothing in the record to show that the foreigner''s complaint was justified and the Chief of Police strongly denied the accusation.', 'http://static2.iyuba.cn/newconcept/british/3/3_53.mp3', '材料中没有任何文字记载证明外国人所说的情况符合事实，警察局长矢口否认这一指控。', '160.8', 3053, 'accusation', 0, 0, 3, 2053, 2825);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/40/42/40424976ec44205ee357f4e3a2cf4bfa.mp3', 'v. 查出，查明', '190.4', '27', 15, 'ˌæsə ˈteɪn', 'The lawyer ascertained that a policeman had indeed dealt roughly with foreigners on several occasions.', 'http://static2.iyuba.cn/newconcept/british/3/3_53.mp3', '律师证实有个警察确实多次粗鲁地对待外国人。', '183.6', 3053, 'ascertain', 0, 0, 3, 2053, 2826);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/0d/26/0d265ad8674a262692afd6f5b112e73c.mp3', 'adj. 有偏见的，不公平的', '197.2', '28', 16, 'ˈpredʒədɪst', 'The fact that the policeman was prejudiced against foreigners could not be recorded in the official files.', 'http://static2.iyuba.cn/newconcept/british/3/3_53.mp3', '警察歧视外国人的事在官方档案中不可能加以记载，', '191.0', 3053, 'prejudiced', 0, 0, 3, 2053, 2827);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/4a/e3/4ae35dbb42614d2429b7d6d181a950bb.mp3', 'adj. 即时的', '221.1', '31', 17, 'prɒmpt', 'The Ombudsman''s prompt action at once put an end to an unpleasant practice which might have gone unnoticed.', 'http://static2.iyuba.cn/newconcept/british/3/3_53.mp3', '司法特派员及时采取的行动，迅速制止了这一起不愉快的事件，不然这件事可能因未得到人们注意而不了了之。', '214.5', 3053, 'prompt', 0, 0, 3, 2053, 2828);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/dc/e4/dce41a93f7edb175dfc59a4d52105847.mp3', 'n. 昆虫', '23.8', '4', 1, 'ˈɪnsekt', 'We have been brought up to fear insects.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '我们自幼就在对昆虫的惧怕中长大。', '21.0', 3054, 'insect', 0, 0, 3, 2054, 2829);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/85/44/8544ce027c736e774b7c46cd6442c93c.mp3', 'v. 进行（斗争）', '36.4', '6', 2, 'weɪdʒ', 'We continually wage war on them, for they contaminate our food, carry diseases, or devour our crops.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '人类不断地同昆虫斗争，因为昆虫弄脏我们的食物，传播疾病，吞噬庄稼。', '29.1', 3054, 'wage', 0, 0, 3, 2054, 2830);
