INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/f1/a8/f1a81d782dea6a19bdca383bffe68452.mp3', 'n. 蜘蛛', '55.4', '9', 5, 'ˈspaɪdə(r)', 'We live in dread not only of unpleasant insects like spiders or wasps, but of quite harmless ones like moths.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '我们在日常生活中，不但憎恶如蜘蛛、黄蜂之类令人讨厌的昆虫，而且憎恶并无大害的飞蛾等。', '47.0', 3054, 'spider', 0, 0, 3, 2054, 2833);
