INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/92/5f/925f7a7719d7a9579cfdf4a940a82d70.mp3', 'n. 蚂蚁', '69.8', '11', 8, 'ænt', 'Knowing that the industrious ant lives in a highly organized society does nothing to prevent us from being filled with revulsion', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '即使知道勤奋的蚂蚁生活在具有高度组织性的社会里，（-- -- 当看到大群蚂蚁在我们精心准备的午间野餐上爬行时，）我们也无法抑制对它们的反感。', '61.3', 3054, 'ant', 0, 0, 3, 2054, 2836);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/d5/2f/d52fa280ada7334475f43a8298065cfb.mp3', 'n. 厌恶', '69.8', '11', 9, 'rɪ ˈvʌlʃn', 'Knowing that the industrious ant lives in a highly organized society does nothing to prevent us from being filled with revulsion', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '即使知道勤奋的蚂蚁生活在具有高度组织性的社会里，（-- -- 当看到大群蚂蚁在我们精心准备的午间野餐上爬行时，）我们也无法抑制对它们的反感。', '61.3', 3054, 'revulsion', 0, 0, 3, 2054, 2837);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/29/dc/29dcbf9faad5165574550187a8615c44.mp3', 'n. 群', '74.8', '12', 10, 'hɔ:d, həʊrd', 'when we find hordes of them crawling over a carefully prepared picnic lunch.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '(.. .. 当看到大群蚂蚁在我们精心准备的午间野餐上爬行时，)', '69.8', 3054, 'horde', 0, 0, 3, 2054, 2838);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/2a/0a/2a0a11a19742e34eaa7f0041d05f3e48.mp3', 'adj. 神秘的，不可思议的', '82.8', '13', 11, 'ʌn ˈkænɪ:', 'No matter how much we like honey, or how much we have read about the uncanny sense of direction which bees possess,', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '不管我们多么爱吃蜂蜜，或读过多少关于蜜蜂具有神秘的识别方向的灵感的书，', '74.8', 3054, 'uncanny', 0, 0, 3, 2054, 2839);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/26/eb/26eb25b14d930f9d5f59b2c50798a9a4.mp3', 'v. 擦，抹去', '91.1', '15', 12, 'ɪ ˈreɪz', 'Most of our fears are unreasonable, but they are impossible to erase.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '我们的恐惧大部分是没有道理的，但却无法消除。', '86.2', 3054, 'erase', 0, 0, 3, 2054, 2840);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/4/e/4/4e4f84abb6e179a17047c848ed290155.mp3', '螳螂', '105.0', '17', 13, 'ˌpreɪɪŋ ˈmæntɪs', 'We enjoy reading about them, especially when we find that, like the praying mantis, they lead perfectly horrible lives.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '我们喜欢看有关昆虫的书，尤其是当我们了解到螳螂等过着一种令人生畏的生活时，就更加爱读有关昆虫的书了。', '96.7', 3054, 'praying mantis', 0, 0, 3, 2054, 2841);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/5/3/1/5316a0605068f2a9b333955fdff135e6.mp3', 'adj. 出神的', '113.0', '18', 14, 'en''trɑ:nst', 'We enjoy staring at them, entranced as they go about their business, unaware (we hope) of our presence.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '我们喜欢入迷地看它们做事，它们不知道（但愿如此）我们就在它们身边。', '105.5', 3054, 'entranced', 0, 0, 3, 2054, 2842);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/8f/2a/8f2a77cef9ebb10c345ac04ab24771e3.mp3', 'n. 甲虫', '123.4', '19', 15, 'ˈbiːtl', 'Who has not stood in awe at the sight of a spider pouncing on a fly, or a column of ants triumphantly bearing home an enormous dead beetle?', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '当看到蜘蛛扑向一只苍蝇时，一队蚂蚁抬着一只巨大的死甲虫凯旋归时，谁能不感到敬畏呢?', '113.4', 3054, 'beetle', 0, 0, 3, 2054, 2843);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/7d/8c/7d8c842725956b1d76c51f69f4a71f8f.mp3', 'adj. 伤不着的，无危险的', '136.5', '21', 16, 'ˈʃeltəd', 'The tree has grown against a warm wall on a sheltered side of the house.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '那棵树是靠着房子有遮挡的一面暖墙生长的。', '132.3', 3054, 'sheltered', 0, 0, 3, 2054, 2844);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/db/32/db323cb0cc2d72721b02fb6ac2b3e189.mp3', 'adj. 甘美的', '146.6', '22', 17, 'ˈlʌʃəs', 'I am especially proud of it, not only because it has survived several severe winters, but because it occasionally produces luscious peaches.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '我为这棵树感到特别自豪，不仅因为它度过了几个寒冬终于活了下来，而且还因为它有时结出些甘甜的桃子来。', '137.0', 3054, 'luscious', 0, 0, 3, 2054, 2845);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/06/b2/06b2d4b91b5c9eaa8c20a1c270f95b3c.mp3', 'n. 一簇', '158.5', '24', 18, 'ˈklʌstə(r)', 'Clusters of tiny insects called aphids were to be found on the underside of the leaves.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '结果在树叶背面找到成串的叫作蚜虫的小虫子。', '152.2', 3054, 'cluster', 0, 0, 3, 2054, 2846);
