INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/09/2e/092e37b684bc3680545e46380e31c831.mp3', 'n. 底面，下侧', '158.5', '24', 20, 'ˈʌndəsaɪd', 'Clusters of tiny insects called aphids were to be found on the underside of the leaves.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '结果在树叶背面找到成串的叫作蚜虫的小虫子。', '152.2', 3054, 'underside', 0, 0, 3, 2054, 2848);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/05/62/056237233fdb50653ad5f1541fcbe621.mp3', 'n. 一群', '164.4', '25', 21, 'ˈkɒləni', 'They were visited by a large colony of ants which obtained a sort of honey from them.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '蚜虫遭到一窝蚂蚁的攻击，蚂蚁从它们身上可以获得一种蜜。', '159.3', 3054, 'colony', 0, 0, 3, 2054, 2849);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/60/9a/609a8f6f218fdfe6f955e19f818ec050.mp3', 'adj. 粘的', '179.7', '27', 22, 'ˈstɪkɪ', 'I bound the base of the tree with sticky tape, making it impossible for the ants to reach the aphids.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '我用一条胶带把桃树底部包上，不让蚂蚁接近蚜虫。', '173.7', 3054, 'sticky', 0, 0, 3, 2054, 2850);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/e8/80/e880c25b8b19bd8d00d6104006e93fd4.mp3', 'v. 小步跑', '189.2', '29', 23, 'ˈskʌrɪ:, ˈskɜ:rɪ:', 'For a long time. I watched them scurrying around the base of the tree in bewilderment.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '在很长一段时间里，我看见蚂蚁围着大树底部来回转悠，不知所措。', '184.1', 3054, 'scurry', 0, 0, 3, 2054, 2851);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/6b/3d/6b3d43184271885b9fb260c59821d5e8.mp3', 'v. 聚集', '201.2', '30', 24, 'swɔ:m', 'I even went out at midnight with a torch and noted with satisfaction (and surprise) that the ants were still swarming around the sticky tape without being able to do anything about it.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '半夜，我还拿着电筒来到花园里，满意地（同时惊奇地）发现那些蚂蚁还围着胶带团团转，无能为力。', '189.8', 3054, 'swarm', 0, 0, 3, 2054, 2852);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/ef/87/ef874c9e77e86cc6ffb17fa30a855565.mp3', 'n. 机灵', '222.5', '34', 25, 'ˌɪndʒɪ ˈnjʊɪtɪ', 'I realized sadly that I had been completely defeated by their ingenuity.', 'http://static2.iyuba.cn/newconcept/british/3/3_54.mp3', '我懊丧地感到败在了足智多谋的蚂蚁手下。', '217.3', 3054, 'ingenuity', 0, 0, 3, 2054, 2853);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/a1/6b/a16b3838ed23bc5c11c449a2e8c9f327.mp3', 'n. 天文学', '28.3', '4', 1, 'ə ˈstrɒnəmi', 'Recent developments in astronomy have made it possible to detect planets in our own Milky Way and in other galaxies.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '天文学方面最新发展使得我们能够在银河系和其他星系发现行星。', '20.6', 3055, 'astronomy', 0, 0, 3, 2055, 2854);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/99/c4/99c483e1d11b1a279a2a1a3960528487.mp3', 'adj. 相对的', '36.8', '5', 2, 'ˈrelətɪv', 'This is a major achievement because, in relative terms, planets are very small and do not emit light.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '这是一个重要的成就，因为相对来说，行星很小，而且也不发光。', '29.3', 3055, 'relative', 0, 0, 3, 2055, 2855);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/ad/67/ad67764102627cfae6d733a1a5fccfdf.mp3', 'adv. 无限地，无穷地', '44.5', '6', 3, '’ɪnfənɪtlɪ', 'Finding planets is proving hard enough, but finding life on them will prove infinitely more difficult.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '寻找行星证明相当困难，但是要在行星上发现生命会变得无比艰难。', '37.6', 3055, 'infinitely', 0, 0, 3, 2055, 2856);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/c6/6e/c66ef8e0ba12ce6624bc615e7b21926d.mp3', 'adj. 太阳系的', '59.7', '8', 4, 'ˈsəʊlə(r)', 'In our own solar system, for example, Venus is far too hot and Mars is far too cold to support life.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '举例来说，在我们的太阳系里，对于生命来说，金星的温度太高，而火星的温度则太低。', '51.0', 3055, 'solar', 0, 0, 3, 2055, 2857);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://static2.iyuba.cn/aiciaudio/Venus.mp3', 'n. 金星', '59.7', '8', 5, 'ˈviːnəs', 'In our own solar system, for example, Venus is far too hot and Mars is far too cold to support life.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '举例来说，在我们的太阳系里，对于生命来说，金星的温度太高，而火星的温度则太低。', '51.0', 3055, 'Venus', 0, 0, 3, 2055, 2858);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/6/8/8/68834fdcdff2a84a2844bd794aa9bcdf.mp3', 'n. 火星', '59.7', '8', 6, 'mɑ:z', 'In our own solar system, for example, Venus is far too hot and Mars is far too cold to support life.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '举例来说，在我们的太阳系里，对于生命来说，金星的温度太高，而火星的温度则太低。', '51.0', 3055, 'Mars', 0, 0, 3, 2055, 2859);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/11/9d/119d6ce059a84cbf83227a350594e466.mp3', 'n. 运行轨道', '103.7', '13', 7, 'ˈɔ:bɪt', 'Alternatively, if the star were small, the life-supporting planet would have to have a close orbit round it and also provide the perfect conditions for life forms to develop.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '反之，如果恒星很小，维持生命的行星就要在离恒星很近的轨道上运行，而且要有极好的条件才能使生命得以发展。', '92.8', 3055, 'orbit', 0, 0, 3, 2055, 2860);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/97/a3/97a3c5620216a5ff78d002992cc6e7b6.mp3', 'adj. 天文学的', '120.5', '16', 8, 'ˌæstrə''nɔmɪkəl', 'The development of such a telescope will be one of the great astronomical projects of the twenty-first century.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '而开发这样一台望远镜将会是21世纪天文学的一个重要的研究课题。', '113.7', 3055, 'astronomical', 0, 0, 3, 2055, 2861);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/15/11/15117b282328146ac6afebaa8acd80e7.mp3', 'v. 产生', '135.4', '18', 9, 'ˈdʒenəreɪt', 'Our own warm atmosphere and the heat generated by the telescope would make it impossible to detect objects as small as planets.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '地球周围温暖的大气层和望远镜散出的热量使得我们根本不可能找到比行星更小的物体。', '127.1', 3055, 'generate', 0, 0, 3, 2055, 2862);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/19/2b/192b4db65193f2019f3c4b25dadbae41.mp3', 'n. 微粒，粒子', '146.7', '19', 10, 'ˈpɑ:tɪkl', 'Even a telescope in orbit round the earth, like the very successful Hubble telescope, would not be suitable because of the dust particles in our solar system.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '即使是一台放置在围绕地球的轨道上的望远镜--如非常成功的哈勃望远镜--也因为太阳系中的尘埃微粒而无法胜任。', '136.4', 3055, 'particle', 0, 0, 3, 2055, 2863);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/a6/14/a6145ec023fca04bcd7ff1b2bd98ed03.mp3', 'n. 木星', '154.1', '20', 11, 'ˈdʒuːpɪtə(r)', 'A telescope would have to be as far away as the planet Jupiter to look for life in outer space,', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '望远镜要放置在木星那样遥远的行星上才有可能在外层空间搜寻生命，', '146.7', 3055, 'Jupiter', 0, 0, 3, 2055, 2864);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/36/59/3659bc427c002e4a78baba066d4909c1.mp3', 'v. 遮暗', '167.1', '22', 12, 'blɒt', 'Once we detected a planet, we would have to find a way of blotting out the light from its star,', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '一旦我们找到这样一颗行星，我们就要想办法将它的恒星射过来的光线遮暗，', '161.5', 3055, 'blot', 0, 0, 3, 2055, 2865);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/ba/d4/bad47b251ce2232bd69b2445aca3332b.mp3', 'v. 分析', '172.1', '23', 13, 'ˈænəlaɪz', 'so that we would be able to ''see'' the planet properly and analyse its atmosphere.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '这样我们就能彻底“看见”这颗行星，并分析它的大气层。', '167.4', 3055, 'analyse', 0, 0, 3, 2055, 2866);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c4/94/c49441ae499275863a02b96813fde8d9.mp3', 'n. 细菌', '184.4', '25', 14, 'bæk ˈtɪərɪə', 'The life forms most likely to develop on a planet would be bacteria.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '行星上最容易生存下来的是细菌。', '179.8', 3055, 'bacteria', 0, 0, 3, 2055, 2867);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/61/24/6124d98749365e3db2c9e5b27ca04db6.mp3', 'n. 氧气', '189.4', '26', 15, 'ˈɔksɪdʒən', 'It is bacteria that have generated the oxygen we breathe on earth.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '正是细菌生产出我们在地球上呼吸的氧气。', '184.4', 3055, 'oxygen', 0, 0, 3, 2055, 2868);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/b9/4b/b94b7ef7f17d2394d6fbdf458dadc7b0.mp3', 'n. 领域', '208.8', '29', 16, 'relm', 'But this hope is always in the realms of science fiction.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '但是，这种希望总是只在科幻小说中存在。', '205.4', 3055, 'realm', 0, 0, 3, 2055, 2869);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/f9/62/f962b6c33de69e53bbcd7140d9d40f3b.mp3', 'v. 努力', '230.0', '32', 17, 'ɪn ˈdevə(r)', 'No human endeavour or thought would be unchanged by it.', 'http://static2.iyuba.cn/newconcept/british/3/3_55.mp3', '任何人类的努力和想法都会发生变化。”', '226.2', 3055, 'endeavour', 0, 0, 3, 2055, 2870);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/81/fd/81fd830c85363675edb98d2879916d8c.mp3', 'n. 界线；边界', '25.3', '4', 1, 'ˈbaʊndərɪ', 'The river which forms the eastern boundary of our farm has always played an important part in our lives.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '形成我们农场东部边界的一条河流一直在我们生活中发挥着重要作用。', '18.5', 3056, 'boundary', 0, 0, 3, 2056, 2871);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://static2.iyuba.cn/aiciaudio/pump.mp3', 'v. 用泵抽', '35.6', '6', 2, 'pʌmp', 'There is only enough spring water to supply the needs of the house, so we have to pump from the river for farm use.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '泉水只能满足家庭生活用水，因此我们必须从河里抽水以用于农业生产。', '28.3', 3056, 'pump', 0, 0, 3, 2056, 2872);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/4c/b1/4cb1d9d9f6042f824315ff9a493e6e83.mp3', 'v. 突然降临', '48.8', '9', 3, 'ˌəʊvə ˈteɪk', 'that misfortune might overtake us if the important events of our lives were not related to it.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '要是我们不把生活中的重大事件告诉那条河，就可能大祸临头。', '42.2', 3056, 'overtake', 0, 0, 3, 2056, 2873);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/bf/6b/bf6b34778a0b56592c20792ca2464ffe.mp3', 'n. 回水河汊', '56.7', '11', 4, 'ˈbækwɔ:tə(r)', 'Sometimes we go upstream to a favourite backwater,', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '有时，我们溯流而上来到我们喜爱的回水河汊举办；', '53.2', 3056, 'backwater', 0, 0, 3, 2056, 2874);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/4c/c4/4cc4206053690313d59fd13c11761a72.mp3', 'n. 前任，前辈', '67.5', '13', 5, 'ˈpriːdəsesə(r)', 'which a predecessor of ours at the farm built in the meadow hard by the deepest pool for swimming and diving.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '那船坞是农场一位前辈在一块草地上盖的，草地紧挨着一个专供游泳、跳水的深水池。', '60.3', 3056, 'predecessor', 0, 0, 3, 2056, 2875);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/07/a4/07a420357eafd0f0c1e5bdb73950d061.mp3', 'n. 草地，草场', '67.5', '13', 6, 'ˈmedəʊ', 'which a predecessor of ours at the farm built in the meadow hard by the deepest pool for swimming and diving.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '那船坞是农场一位前辈在一块草地上盖的，草地紧挨着一个专供游泳、跳水的深水池。', '60.3', 3056, 'meadow', 0, 0, 3, 2056, 2876);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/40/8e/408e69f65e4ddaf550331c22567de3ca.mp3', 'v. 给……戴花环', '81.3', '15', 7, 'kraʊn', 'We welcome the seasons by the riverside, crowning the youngest girl with flowers in the spring,', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '我们在河边迎接一年四季。春天在河边为最年轻的姑娘戴上花冠，', '75.7', 3056, 'crown', 0, 0, 3, 2056, 2877);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/68/24/6824cc4c7c33aabf02553093853c2e69.mp3', 'n. 一种冬青植物', '91.7', '17', 8, 'ˈhɒli', 'and throwing a holly wreath into the current in the winter.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '冬天往河中抛撒一个冬青花环。', '88.0', 3056, 'holly', 0, 0, 3, 2056, 2878);
