INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/98/6c/986cb16e987a0c566139f6c91e151055.mp3', 'n. 部分', '110.9', '20', 11, 'prə ˈpɔːʃn', 'We are lucky in that only the lower fields, which make up a very small proportion of our farm, are affected by flooding,', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '值得庆幸的是，只有低洼地受到洪水影响，而低洼地在我们农场比例很小。', '103.2', 3056, 'proportion', 0, 0, 3, 2056, 2881);
