INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/4d/0e/4d0e9c40af5450bd67e8085d2a7f0ff5.mp3', 'n. 牲口棚', '128.4', '23', 14, 'stɔ:l', 'All the cattle had been moved into stalls and we stood to lose little.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '所有的牲口已提前转移到畜圈里，没有造成什么损失。', '124.2', 3056, 'stall', 0, 0, 3, 2056, 2884);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/05/35/0535c94e1cddad7ca2b919fb9c89f77e.mp3', 'n. 顶楼', '149.5', '26', 15, 'ˈætɪk', 'From an attic window we could get a sweeping view of the river where their land joined ours,', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '从顶楼窗口看去，我们农场与他们农场接壤处的那段河流一览无余。', '143.9', 3056, 'attic', 0, 0, 3, 2056, 2885);
