INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/2f/d2/2fd2520c380bb75725067d50f3d61c48.mp3', 'n. 木筏', '180.3', '31', 19, 'rɑ:ft', 'Suddenly a raft appeared, looking rather like Noah''s ark, carrying the whole family, a few hens, the dogs, a cat, and a bird in a cage.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '突然，出现了一只筏子，看起来很像诺亚方舟，上面载着他们全家老小，还有几只母鸡、几只狗、一只猫与一只鸟笼，那里头有一只小鸟。', '169.2', 3056, 'raft', 0, 0, 3, 2056, 2889);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/55/66/55668f78b8cf0bed357d578828a41ad9.mp3', 'adv. 过度地', '185.8', '32', 20, 'ʌn''djʊ:lɪ', 'We realized that they must have become unduly frightened by the rising flood,', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '我们意识到他们一定是被不断上涨的洪水吓坏了。', '181.1', 3056, 'unduly', 0, 0, 3, 2056, 2890);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/4/2/5/42598cc00ae7b8f12543d9e9c38b6c35.mp3', 'n. 地基', '193.8', '33', 21, 'fɑʊn''deɪʃəns', 'for their house, which had sound foundations, would have stood stoutly even if it had been almost submerged.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '因为他们的房子地基牢固，即使洪水几乎灭顶也不会倒塌。', '186.0', 3056, 'foundations', 0, 0, 3, 2056, 2891);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/1/d/4/1d4ef21dd121dc5d73afd025c32098b9.mp3', 'adv. 牢固地，粗壮的', '193.8', '33', 22, 'staʊtlɪ', 'for their house, which had sound foundations, would have stood stoutly even if it had been almost submerged.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '因为他们的房子地基牢固，即使洪水几乎灭顶也不会倒塌。', '186.0', 3056, 'stoutly', 0, 0, 3, 2056, 2892);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/86/90/86904b17b35cd5a92723b971cf76a47d.mp3', 'v. 浸没', '193.8', '33', 23, 'səb ˈmɜːdʒ', 'for their house, which had sound foundations, would have stood stoutly even if it had been almost submerged.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '因为他们的房子地基牢固，即使洪水几乎灭顶也不会倒塌。', '186.0', 3056, 'submerge', 0, 0, 3, 2056, 2893);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/90/d7/90d7f1687b4037a07611287f41499cdf.mp3', 'v. 抓住', '205.0', '34', 24, 'ˈɡræpl', 'The men of our family waded down through our flooded meadows with boathooks,in the hope of being able to grapple a corner of the raft and pull it out of the current towards our bank.', 'http://static2.iyuba.cn/newconcept/british/3/3_56.mp3', '我家的男人们手拿船篙蹚过被水淹没的草场，希望能够钩住筏子一角，将它拽出激流，拖回岸边。', '194.6', 3056, 'grapple', 0, 0, 3, 2056, 2894);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/d5/6f/d56fd732f0d15de62776ae888ecc544a.mp3', 'adj. 异国的，外国的', '28.4', '5', 1, '’eɪlɪən', 'I had expected to be near my objective by now, but everything still seemed alien to me.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '我本想离目的地已经不远，但周围一切对我仍很陌生。', '22.7', 3057, 'alien', 0, 0, 3, 2057, 2895);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c2/84/c28482f01e6761e0dd6ffdfac0c5991a.mp3', 'v. 移居（国外）', '51.4', '9', 2, 'ˈemɪɡreɪt', 'So he decided to emigrate.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '于是他决定移居他国。', '49.6', 3057, 'emigrate', 0, 0, 3, 2057, 2896);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/51/85/5185afb632a22ee838a6ddbe11efe57e.mp3', 'v. 全神贯注于', '59.7', '10', 3, 'əb ˈsɔ:b', 'In the new country he became absorbed in making a new life for the two of us, so that he gradually ceased to grieve.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '在这个新的国家里，父亲专心致志地为我们俩开创一种新的生活，慢慢地不伤心了。', '52.1', 3057, 'absorb', 0, 0, 3, 2057, 2897);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/60/5a/605abe26d014c72e3df9deb267e73756.mp3', 'adj. 扎牢的', '79.3', '14', 4, 'ɪm''bedɪd', 'His roots and mine had become too firmly embedded in the new land.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '因为他与我一样已经把根深深地扎在了异国的土地上。', '74.1', 3057, 'embedded', 0, 0, 3, 2057, 2898);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/4c/7d/4c7de52faddaaca02c297dc58e1836a6.mp3', 'adv. 致命地', '93.9', '16', 5, 'ˈmɔ:təlɪ', 'He became mortally ill a few months before we had planned to go and, when he knew that he was dying, he made me promise to go on my own.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '就在他计划回国的前几个月，他突然身患绝症。他知道自己已奄奄一息，于是他要我答应一定单独回故乡一趟。', '85.0', 3057, 'mortally', 0, 0, 3, 2057, 2899);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/30/83/308319a5ba52f2b73fd79195accdd23a.mp3', 'adj. 广泛的；丰富的', '100.0', '17', 6, 'ˌkɒmprɪ ˈhensɪv', 'I hired a car the day after landing and bought a comprehensive book of maps,', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '我下飞机后租了一辆车，并买了一本详尽的地图册。', '94.8', 3057, 'comprehensive', 0, 0, 3, 2057, 2900);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/48/02/4802fad13618b3ecb36d9180e981704f.mp3', 'n. 里程碑', '119.5', '20', 7, 'ˈmaɪl ˌstəʊn', 'But my father had described over and over again what we should see at every milestone, after leaving the nearest town,', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '而是父亲曾详细给我讲了，在过了离故乡最近的那个小镇后，在每一个路标处可见到些什么。', '111.4', 3057, 'milestone', 0, 0, 3, 2057, 2901);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/b1/5b/b15bf92e8d1a16d7b5f725e70cbee681.mp3', 'n. 领地；地区', '124.1', '21', 8, 'ˈterɪtərɪ', 'so that I was positive I should recognize it as familiar territory.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '因此，我相信这段路对我来说会是很熟悉的。', '119.5', 3057, 'territory', 0, 0, 3, 2057, 2902);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://static2.iyuba.cn/aiciaudio/milometer.mp3', 'n. 计程表', '133.1', '23', 9, 'maɪˈlɑːmɪtər', 'I looked at the map and then at the milometer.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '我看了看地图，又查了一下里程表。', '129.9', 3057, 'milometer', 0, 0, 3, 2057, 2903);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/11/3d/113d30194a10f96ef87f478f25cb3213.mp3', 'n. （教堂的）塔尖', '147.8', '26', 10, 'ˈspaɪə(r)', 'with the spire of the church of our village showing in the far distance.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '还可远远望见老家村子里的教堂的尖顶。', '143.5', 3057, 'spire', 0, 0, 3, 2057, 2904);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/df/7a/df7ad663c9db65863d06a08c2a4d073a.mp3', 'v. 返回，重走', '167.2', '29', 11, 'rɪ: ˈtreɪs', 'So I drove back to the town and began to retrace the route, taking frequent glances at the map.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '于是我驾车返回小镇，重新按路线行驶。', '160.9', 3057, 'retrace', 0, 0, 3, 2057, 2905);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/cd/25/cd25fb90ab87ddfc7990707be0ef3f5a.mp3', 'v. 趔趄地走', '181.9', '32', 12, '‘stʌmbl', 'I felt as if I had stumbled into a nightmare country, as you sometimes do in dreams.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '我感到自己就像平时作梦那样迷迷糊糊地闯进了恶梦境地。', '176.2', 3057, 'stumble', 0, 0, 3, 2057, 2906);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/40/7e/407e3b6b8b88aaf095f15efeb865efd6.mp3', 'n. 地平线', '195.9', '34', 13, 'hə ˈraɪzn', 'Fortunately for me, as I was wondering what to do next, there appeared on the horizon a man on horseback, riding in my direction.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '不过，我是幸运的，正当我走投无路之时，从天边出现一个骑马的人向我骑来。', '187.0', 3057, 'horizon', 0, 0, 3, 2057, 2907);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/26/96/26967d78853207f75c23513997d737d7.mp3', 'n. 水库', '226.3', '40', 14, 'ˈrezəvwɑ:(r)', 'The lake was not a natural one, but a man-made reservoir.', 'http://static2.iyuba.cn/newconcept/british/3/3_57.mp3', '这不是一个天然湖泊，是一座人工修建的水库。', '221.3', 3057, 'reservoir', 0, 0, 3, 2057, 2908);
