INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/0/c/2/0c2138b253f793f0cf10e65ffe5785d1.mp3', 'n. 标本', '209.1', '30', 14, 'ˈspesɪmən', 'Soon the hobby leads to travel, perhaps to a meeting in another town, possibly a trip abroad in search of a rare specimen,', 'http://static2.iyuba.cn/newconcept/british/3/3_59.mp3', '用不了多久，有这种爱好的人便开始旅行，也许是去另一个城市参加会议，也可能是出国寻找一件珍品，', '200.3', 3059, 'specimen', 0, 0, 3, 2059, 2941);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/e/e/b/eeb655815bb056d40d202fd22bd7609c.mp3', 'adv. 有益的，积极的', '248.4', '35', 15, 'kən''strʌktɪvlɪ', 'Collecting, by occupying spare time so constructively, makes a person contented, with no time for boredom.', 'http://static2.iyuba.cn/newconcept/british/3/3_59.mp3', '收藏活动通过富有建设性地利用业余时间使人感到心满意足，不再有无聊之日。', '236.5', 3059, 'constructively', 0, 0, 3, 2059, 2942);
