INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/1d/57/1d57e3e6ad1f0e4e6e575389892c5d64.mp3', 'n.迁移，移居', '54.9', '8', 4, 'mai''greiʃn', 'These legends are useful because they can tell us something about migrations of people who lived long ago,', 'http://static2.iyuba.cn/newconcept/british/4/4_1.mp3', '这些传说是有用的，因为他们告诉我们很久以前生活在这里的移民的一些事情。', '46.8', 4001, 'migration', 0, 0, 4, 3001, 2962);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c4/cc/c4cc9e3dd15447092ae92ff493b307e1.mp3', 'n.人类学家', '68.8', '10', 5, 'ˌænθrə''pɔlədʒist', 'Anthropologists wondered where the remote ancestors of the Polynesian peoples now living in the Pacific Islands came from.', 'http://static2.iyuba.cn/newconcept/british/4/4_1.mp3', '人类学家过去不清楚如今生活在太平洋诸岛上的波利尼西亚人的祖先来自何方。', '60.3', 4001, 'anthropologist', 0, 0, 4, 3001, 2963);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/24/e3/24e3e3c61e2eb34d22fa3a6e092fa52c.mp3', 'n.考古学家', '97.3', '13', 6, 'ˌɑ:ki''ɔlədʒist', 'So archaeologists have neither history nor legends to help them to find out where the first ''modern men'' came from.', 'http://static2.iyuba.cn/newconcept/british/4/4_1.mp3', '于是，考古学家们既缺乏历史记载，又无口头传说来帮助他们弄清最早的“现代人”是从哪里来的。', '89.0', 4001, 'archaeologist', 0, 0, 4, 3001, 2964);
