INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/e3/49/e349b545d9f72d801dddebfd7d1a4ce2.mp3', 'n.燧石', '105.0', '14', 10, 'flɪnt', 'Fortunately, however, ancient men made tools of stone, especially flint,', 'http://static2.iyuba.cn/newconcept/british/4/4_1.mp3', '然而， 幸运的是，远古人用石头制作了工具，特别是用燧石，', '99.0', 4001, 'flint', 0, 0, 4, 3001, 2968);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/0d/3d/0d3d238b089a67e34e39b5abf80db19b.mp3', 'v.烂掉', '115.3', '16', 11, 'rɔt', 'They may also have used wood and skins, but these have rotted away.', 'http://static2.iyuba.cn/newconcept/british/4/4_1.mp3', '他们也可能用过木头和兽皮，但这类东西早已腐烂殆尽。', '110.1', 4001, 'rot', 0, 0, 4, 3001, 2969);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/b8/78/b878bc127cdb631d4fea3580fccceaa0.mp3', 'n.野兽', '51.9', '9', 1, 'bi:st', 'We owe a lot to the birds and beasts who eat insects', 'http://static2.iyuba.cn/newconcept/british/4/4_2.mp3', '我们要十分感谢那些吃昆虫的鸟和兽，', '47.7', 4002, 'beast', 0, 0, 4, 3002, 2970);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/64/68/6468b083c5509c8a79c979c6b0c16add.mp3', 'n.统计数字', '97.3', '15', 2, '''sensəs', 'One authority on spiders made a census of the spiders in a grass field in the south of England,', 'http://static2.iyuba.cn/newconcept/british/4/4_2.mp3', '一位研究蜘蛛的权威对英国南部一块草坪上的蜘蛛作了一次调查。', '91.1', 4002, 'census', 0, 0, 4, 3002, 2971);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/be/06/be0608aeaa6a12e4b25c011ad358c0e8.mp3', 'n.英亩', '104.8', '16', 3, 'ˈeikə(r)', 'and he estimated that there were more than 2, 250, 000 in one acre;', 'http://static2.iyuba.cn/newconcept/british/4/4_2.mp3', '他估计每英亩草坪里有225万多只蜘蛛。', '98.2', 4002, 'acre', 0, 0, 4, 3002, 2972);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/9a/03/9a0364b9e99bb480dd25e1f0284c8555.mp3', 'a.满足的', '128.1', '19', 4, 'ˈkɔntent', 'It is impossible to make more than the wildest guess at how many they kill, but they are hungry creatures, not content with only three meals a day.', 'http://static2.iyuba.cn/newconcept/british/4/4_2.mp3', '它们一年中消灭了多少昆虫，我们简直无法猜测，它们是吃不饱的动物，不满意一日三餐。', '118.1', 4002, 'content', 0, 0, 4, 3002, 2973);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/e4/b3/e4b30aa42916a294e3c77da5339eeb20.mp3', 'n.马特霍恩峰(阿尔卑斯山峰之一)', '4.6', '1', 1, 'ˈmætə:ˌhɔ:n', 'Lesson 3 Matterhorn man', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '课文3  马特霍恩山区人', '1.6', 4003, 'Matterhorn', 0, 0, 4, 3003, 2974);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://static2.iyuba.cn/aiciaudio/alpinist.mp3', 'n.登山运动员', '24.7', '4', 2, 'ˈælpɪnɪst', 'Modern alpinists try to climb mountains by a route which will give them good sport,', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '现代登山运动员总想找一条能够给他们带来运动乐趣的路线来攀登山峰。', '18.6', 4003, 'alpinist', 0, 0, 4, 3003, 2975);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/b3/9e/b39ecd658b2b74a2712303acf766b4a7.mp3', 'v.开辟，倡导;n.先锋，开辟者', '33.6', '6', 3, 'ˌpaiəˈniə(r)', 'In the pioneering days however, this was not the case at all.', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '然而，在登山运动的初期，全然不是这种情况。', '30.1', 4003, 'pioneer', 0, 0, 4, 3003, 2976);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/1d/67/1d67a7e5b537452a8b703ded422ae198.mp3', 'n.顶峰', '41.5', '7', 4, '''sʌmit', 'The early climbers were looking for the easiest way to the top because the summit was the prize they sought,', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '早期登山者所寻找的是通往山顶的最方便的途径，因为顶峰才是他们寻求的目标。', '34.4', 4003, 'summit', 0, 0, 4, 3003, 2977);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/6d/2d/6d2de2d9e0d791cbba71867a37fce618.mp3', 'v.到达', '44.8', '8', 5, 'əˈtein', 'especially if it had never been attained before.', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '特别是前人未曾到过的顶峰。', '42.0', 4003, 'attain', 0, 0, 4, 3003, 2978);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/99/b7/99b788c54c1a82651e74c82b5d52c8b4.mp3', 'a.危险的', '53.8', '9', 6, 'ˈperələs', 'It is true that during their explorations they often faced difficulties and dangers of the most perilous nature,', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '确实，在探险中他们经常遇到惊心动魄的困难和危险，', '46.1', 4003, 'perilous', 0, 0, 4, 3003, 2979);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/dc/3d/dc3d60b5b5728549c30d1f83ae25eb0b.mp3', 'v.不寒而栗', '59.3', '10', 7, '''ʃʌdə(r)', 'equipped in a manner which would make a modern climber shudder at the thought,', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '而他们装备之简陋足以使现代登山者一想起来就胆战心惊。', '55.0', 4003, 'shudder', 0, 0, 4, 3003, 2980);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/77/2d/772d6209c825a13e8bd82758db7362e1.mp3', 'v.追求', '63.6', '11', 8, 'kɔ:t', 'but they did not go out of their way to court such excitement.', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '但是，他们并非故意寻求这种刺激，', '59.8', 4003, 'court', 0, 0, 4, 3003, 2981);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/a0/ff/a0ff43c21d3ba8e8cdbea422af35ff14.mp3', 'a.唯一的', '69.6', '12', 9, 'ˈsɔlətəri', 'They had a single aim, a solitary goal -- the top!', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '他们只有一个目标，唯一的目标 -- 顶峰！', '64.5', 4003, 'solitary', 0, 0, 4, 3003, 2982);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/47/06/4706401663db60959ea3113a7f8d14b7.mp3', 'v.使贫困', '91.2', '15', 10, 'ɪmˈpɔvərɪʃ', 'Alpine villages tended to be impoverished settlements cut off from civilization by the high mountains.', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '阿尔卑斯山山区的小村几乎全是高山环抱、与世隔绝的穷乡僻壤。', '84.7', 4003, 'impoverish', 0, 0, 4, 3003, 2983);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/12/23/122326b079ad569a90f7498a20f45a34.mp3', 'a.阿尔卑斯山的', '91.2', '15', 11, '''ælpain', 'Alpine villages tended to be impoverished settlements cut off from civilization by the high mountains.', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '阿尔卑斯山山区的小村几乎全是高山环抱、与世隔绝的穷乡僻壤。', '84.7', 4003, 'Alpine', 0, 0, 4, 3003, 2984);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/5/d/c/5dcc5049891e497094437a3527663715.mp3', 'a.布满跳蚤的', '96.6', '16', 12, 'fli: ''rɪdn', 'Such inns as there were were generally dirty and flea-ridden;', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '那里的小客栈一般都很肮脏，而且跳蚤猖獗。', '92.4', 4003, 'flea-ridden', 0, 0, 4, 3003, 2985);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/a5/81/a581cd23d91ce38a7ae02bd721a91d2f.mp3', 'a.粗劣的', '105.6', '18', 13, 'kɔ:s', 'all washed down with coarse wine.', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '人们就着劣酒吞下这种食物。', '103.2', 4003, 'coarse', 0, 0, 4, 3003, 2986);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c0/9d/c09d7439d3a1551ebf6ae3f5c0da6a7e.mp3', 'v.自恃有', '112.4', '19', 14, 'bəust', 'Often a valley boasted no inn at all, and climbers found shelter wherever they could --', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '山谷里常常没有小客栈，登山者只好随遇而安。', '106.8', 4003, 'boast', 0, 0, 4, 3003, 2987);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/d5/8f/d58f3915605005f9e21333720636db51.mp3', 'n.教区居民', '118.5', '20', 15, 'pəˈrɪʃənə(r)', 'sometimes with the local priest (who was usually as poor as his parishioners),', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '有时同当地牧师 （他通常和他的教民一样穷）住在一起，', '113.0', 4003, 'parishioner', 0, 0, 4, 3003, 2988);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/35/52/355253103c1fe65f33039edfba119049.mp3', 'n.牧羊人', '122.0', '21', 16, '''ʃepəd', 'sometimes with shepherds or cheese-makers.', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '有时同牧羊人或制乳酪的人住在一起。', '119.3', 4003, 'shepherd', 0, 0, 4, 3003, 2989);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/ea/b7/eab7384fed26f209f8d4de9c927e3327.mp3', 'n.亚麻布', '136.5', '23', 17, '''linən', 'For men accustomed to eating seven-course dinners and sleeping between fine linen sheets at home,', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '对于过惯了一顿饭吃7道菜、睡亚麻细布床单的人来说，', '130.7', 4003, 'linen', 0, 0, 4, 3003, 2990);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/e/6/b/e6bfe92a5afd524cef60cc1d0d6dbeed.mp3', 'n.阿尔卑斯山脉', '140.5', '24', 18, 'ði ælps', 'the change to the Alps must have been very hard indeed.', 'http://static2.iyuba.cn/newconcept/british/4/4_3.mp3', '变换一下生活环境来到阿尔卑斯山山区，那一定是很艰难的。', '137.1', 4003, 'the Alps', 0, 0, 4, 3003, 2991);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/ec/03/ec03f91ae56e478455e3786e91559194.mp3', 'a.坚实的', '43.8', '6', 1, 'ˈsɔlid', 'who has normal vision but who can also perceive things with different parts of her skin, and through solid walls.', 'http://static2.iyuba.cn/newconcept/british/4/4_4.mp3', '她的视力与常人一样，但她还能用皮肤的不同部位辨认东西，甚至看穿坚实的墙壁。', '35.8', 4004, 'solid', 0, 0, 4, 3004, 2992);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/9d/fc/9dfc8dce7280fd49fc6e7bf0436ed325.mp3', 'n.保险柜', '54.9', '8', 2, 'seif', 'One day she came into his office and happened to put her hands on the door of a locked safe.', 'http://static2.iyuba.cn/newconcept/british/4/4_4.mp3', '一天，维拉走进父亲的办公室，偶然把手放在一个锁着的保险柜的门上，', '49.1', 4004, 'safe', 0, 0, 4, 3004, 2993);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/07/d3/07d3585f416df885443d636a7aaad54b.mp3', 'n.乌里扬诺夫斯克', '75.6', '11', 3, 'u:ˈljɑ:nɔfsk', 'Vera''s curious talent was brought to the notice of a scientific research institute in the town of Ulyanovsk, near where she lives,', 'http://static2.iyuba.cn/newconcept/british/4/4_4.mp3', '维拉的特异功能引起了她家附近乌里扬诺夫斯克城一个科研单位的注意。', '66.7', 4004, 'Ulyanovsk', 0, 0, 4, 3004, 2994);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/9d/97/9d97e5639a888fa1558512777c1ef587.mp3', 'n.委员会', '84.1', '12', 4, 'kə''miʃn', 'and in April she was given a series of tests by a special commission of the Ministry of Health of the Russian Federal Republic.', 'http://static2.iyuba.cn/newconcept/british/4/4_4.mp3', '4月里，俄罗斯卫生部一个特别委员会对她进行了一系列的测试。', '76.3', 4004, 'commission', 0, 0, 4, 3004, 2995);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/94/61/94619f8a70068b2591c2eed622525b0e.mp3', 'a.不透明的', '92.3', '13', 5, 'əuˈpeik', 'During these tests she was able to read a newspaper through an opaque screen and, stranger still,', 'http://static2.iyuba.cn/newconcept/british/4/4_4.mp3', '在这些测试中，她能隔着不透明的屏幕读报纸，更为奇怪的是，', '85.4', 4004, 'opaque', 0, 0, 4, 3004, 2996);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/fd/5b/fd5b343cd9e5ba382b42e7c90b783cb5.mp3', 'n.一种有编号的纸牌', '100.7', '14', 6, '''lɒtəʊ', 'by moving her elbow over a child''s game of Lotto she was able to describe the figures and colours printed on it;', 'http://static2.iyuba.cn/newconcept/british/4/4_4.mp3', '她把肘部在儿童玩的“罗托”纸牌上移动一下，便能说出印在纸牌上的数字和颜色。', '92.7', 4004, 'lotto', 0, 0, 4, 3004, 2997);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/62/a9/62a9a922613d772f25e8ca22f14c427c.mp3', 'n.拖鞋', '105.5', '15', 7, '’slipə(r)', 'and, in another instance, wearing stockings and slippers,', 'http://static2.iyuba.cn/newconcept/british/4/4_4.mp3', '还有一次，她穿着长筒袜子和拖鞋，', '101.6', 4004, 'slipper', 0, 0, 4, 3004, 2998);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/95/b9/95b9e7d4c6e592b6a64431b0f3c283b4.mp3', 'a.& ad.被蒙上眼睛的', '129.2', '18', 8, '‘blaindfəuld', 'During all these tests Vera was blindfold; and indeed, except when blindfold she lacked the ability to perceive things with her skin.', 'http://static2.iyuba.cn/newconcept/british/4/4_4.mp3', '在所有这些实验中，维拉的双眼都是蒙着的，如果不蒙上双眼她的皮肤就不再具有识别物体的能力。', '119.1', 4004, 'blindfold', 0, 0, 4, 3004, 2999);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/6c/37/6c374e70334072aeeb62ed46ea987838.mp3', 'n.允许', '24.2', '5', 1, 'li:v', 'If there is one -- which I take leave to doubt', 'http://static2.iyuba.cn/newconcept/british/4/4_5.mp3', '如果这个问题存在的话 -- 请允许我对此持怀疑态度。', '21.0', 4005, 'leave', 0, 0, 4, 3005, 3000);
