INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/c6/2f/c62f65308d52fc37c5e1c81f915c47fb.mp3', 'n.友好', '35.2', '4', 1, '''gud''wil', 'I am always amazed when I hear people saying that sport creates goodwill between the nations, and that if only the common peoples of the world could meet one another at football or cricket, they would have no inclination to meet on the battlefield.', 'http://static2.iyuba.cn/newconcept/british/4/4_6.mp3', '我总是惊愕不已。当我听人们说体育运动可创造国家之间的友谊，还说各国民众若在足球场或板球场上交锋，就不愿在战场上残杀的时候。', '18.6', 4006, 'goodwill', 0, 0, 4, 3006, 3017);
