INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/c/3/f/c3ff7c66b5c6127379fd297c76ffeccd.mp3', 'n.比赛', '52.3', '5', 4, 'ˈkɒntest', 'Even if one didn''t know from concrete examples (the 1936 Olympic Games, for instance) that international sporting contests lead to orgies of hatred, one could deduce it from general principles.', 'http://static2.iyuba.cn/newconcept/british/4/4_6.mp3', '一个人即使不能从具体的事例（例如1936年的奥林匹克运动会）了解到国际运动比赛会导致疯狂的仇恨，也可以从常理中推断出结论。', '36.3', 4006, 'contest', 0, 0, 4, 3006, 3020);
