INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/4d/5d/4d5da2ba7e919c0e15ef081a6c5ad8d7.mp3', 'n.尖叫声', '122.2', '14', 14, 'skwi:k', 'It has been found that certain bats emit squeaks and by receiving the echoes,', 'http://static2.iyuba.cn/newconcept/british/4/4_7.mp3', '人们发现，某些蝙蝠能发出尖叫声，并能通过回声', '116.3', 4007, 'squeak', 0, 0, 4, 3007, 3044);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/bb/c6/bbc6db99c0c5e648d564204fdb34bec4.mp3', 'v.屠宰', '23.9', '4', 1, '’slɔ:tə(r)', 'Chickens slaughtered in the United States, claim officials in Brussels, are not fit to grace European tables.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '布鲁塞尔的官员说，在美国屠宰的鸡不适于用来装点欧洲的餐桌。', '16.8', 4008, 'slaughter', 0, 0, 4, 3008, 3045);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/19/77/1977c9daa1d67de51a4651abdb160c09.mp3', 'a.适合', '23.9', '4', 2, 'fit', 'Chickens slaughtered in the United States, claim officials in Brussels, are not fit to grace European tables.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '布鲁塞尔的官员说，在美国屠宰的鸡不适于用来装点欧洲的餐桌。', '16.8', 4008, 'fit', 0, 0, 4, 3008, 3046);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/7f/37/7f3790b4b8e526798f380cbc014d79ff.mp3', 'v.给…增光', '23.9', '4', 3, 'greis', 'Chickens slaughtered in the United States, claim officials in Brussels, are not fit to grace European tables.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '布鲁塞尔的官员说，在美国屠宰的鸡不适于用来装点欧洲的餐桌。', '16.8', 4008, 'grace', 0, 0, 4, 3008, 3047);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/ba/89/ba89f06d695886ace43ea86c3694f73f.mp3', 'n.关税', '42.0', '6', 4, 'ˈtærif', 'These days, it is differences in national regulations, far more than tariffs, that put sand in the wheels of trade between rich countries.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '当前，是各国管理条例上的差异，而不是关税阻碍了发达国家之间的贸易。', '32.6', 4008, 'tariff', 0, 0, 4, 3008, 3048);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c0/0f/c00f0c4675b91fb8b918e4079a0b1bac.mp3', 'n.标准', '64.3', '8', 5, 'ˈstændəd', 'An electric razor that meets the European Union''s safety standards must be approved by American testers before it can be sold in the United States, and an American-made dialysis machine needs the EU''s okay before it hits the market in Europe.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '一把符合欧洲联盟安全标准的电动剃须刀必须得到美国检测人员的认可，方可在美国市场上销售；而美国制造的透析仪也要得到欧盟的首肯才能进入欧洲市场。', '46.5', 4008, 'standard', 0, 0, 4, 3008, 3049);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/93/ea/93ea0f2f912acf07ed0aa2455df4b0c6.mp3', 'n.分离，分解；透析，渗析', '64.3', '8', 6, 'ˌdaɪ''æləsɪs', 'An electric razor that meets the European Union''s safety standards must be approved by American testers before it can be sold in the United States, and an American-made dialysis machine needs the EU''s okay before it hits the market in Europe.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '一把符合欧洲联盟安全标准的电动剃须刀必须得到美国检测人员的认可，方可在美国市场上销售；而美国制造的透析仪也要得到欧盟的首肯才能进入欧洲市场。', '46.5', 4008, 'dialysis', 0, 0, 4, 3008, 3050);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/5a/34/5a34c18fec8a0d9986e587f3f36e03e0.mp3', 'v.使触电身亡', '71.5', '9', 7, 'ɪ''lektrəkju:t', 'As it happens, a razor that is safe in Europe is unlikely to electrocute Americans.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '碰巧在欧洲使用安全的剃须刀不大可能使美国人触电身亡，', '65.5', 4008, 'electrocute', 0, 0, 4, 3008, 3051);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/78/a8/78a8304287043bb9f99ff14af7dc3c90.mp3', 'v.消灭', '91.3', '11', 8, 'iˈlimineit', 'Politicians agree, in principle, so America and the EU have been trying to reach a deal which would eliminate the need to double-test many products.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '政治家在原则上同意了， 因此，美国和欧洲一直在寻求达成协议，以便为许多产品取消双重检查。', '80.8', 4008, 'eliminate', 0, 0, 4, 3008, 3052);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/17/ee/17eec3190b081a1c237f673f39d821a9.mp3', 'n.协议', '123.2', '15', 9, 'əˈkɔ:d', 'The Americans would happily reach one accord on standards for medical devices and then hammer out different pacts covering, say, electronic goods and drug manufacturing.', 'http://static2.iyuba.cn/newconcept/british/4/4_8.mp3', '美国人很愿意就医疗器械的标准达成一个协议，然后推敲出不同的合同，用以涵盖 -- 比如说 -- 电子产品和药品的生产。', '112.6', 4008, 'accord', 0, 0, 4, 3008, 3053);
