INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c8/60/c86060d643ad08681d96e355299a0ee5.mp3', 'v.集合', '98.5', '14', 18, 'əˈsembl', 'The force there assembled was trivial compared with the Danish horde.', 'http://static2.iyuba.cn/newconcept/british/4/4_9.mp3', '他集结在那里的军队和丹麦大军相比是微不足道的，', '94.7', 4009, 'assemble', 0, 0, 4, 3009, 3074);
