INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/db/1b/db1bdcbf205aadd03485509120789522.mp3', 'a.持久的', '110.7', '15', 20, 'prəˈlɒŋd', 'But Alfred had deduced that the Danes were no longer fit for prolonged battle: and that their commissariat had no organization, but depended on irregular raids.', 'http://static2.iyuba.cn/newconcept/british/4/4_9.mp3', '然而，阿尔弗雷德断定，丹麦人已不再适应持久的战争，他们的军需供应处于无组织状态，只是靠临时抢夺来维持。', '99.3', 4009, 'prolonged', 0, 0, 4, 3009, 3076);
