INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/a5/4b/a54b5bcfbf1d8a571f0480a892e71803.mp3', 'v.骚扰', '118.7', '16', 24, '''hæri', 'So, faced with the Danish advance, Alfred did not risk open battle but harried the enemy.', 'http://static2.iyuba.cn/newconcept/british/4/4_9.mp3', '因此，面对丹麦人的进攻，阿尔弗雷德没有贸然同敌人作战，而是采用骚扰敌人的战术。', '111.9', 4009, 'harry', 0, 0, 4, 3009, 3080);
