INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/8e/80/8e80bdd879b5d807aa869f08943bdd75.mp3', 'v.把…记入借方', '89.4', '13', 9, '''debit', 'When, for example, a customer first opens an account, he instructs the bank to debit his account only in respect of cheques drawn by himself.', 'http://static2.iyuba.cn/newconcept/british/4/4_12.mp3', '比如，储户首次在银行开户时，嘱咐银行他的存款只能凭本世人签字的支票来提取。', '79.9', 4012, 'debit', 0, 0, 4, 3012, 3127);
