INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/d1/f7/d1f71c3a760a91626c96b1df5d4d1b5e.mp3', 'n.伪造(文件，签名等)', '109.5', '17', 12, 'ˈfɔ:dʒəri', 'It makes no difference that the forgery may have been a very skillful one:', 'http://static2.iyuba.cn/newconcept/british/4/4_12.mp3', '即使伪造得很巧妙，也不能付款，', '105.0', 4012, 'forgery', 0, 0, 4, 3012, 3130);
