INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/08/b4/08b4b82c5e48997a70d7d36163161106.mp3', 'a.矿物的', '32.9', '5', 1, 'ˈminərəl', 'But we do not need to send man down to get the oil out, as we must with other mineral deposits.', 'http://static2.iyuba.cn/newconcept/british/4/4_13.mp3', '但是，我们不必像开采其他矿藏那样，把人送到地下去把石油取出。', '26.5', 4013, 'mineral', 0, 0, 4, 3013, 3133);
