INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/eb/7a/eb7a99e5f2566772797e55a8bfb8f0a6.mp3', 'n.成效，效力', '25.4', '4', 2, 'i''fektivnis', 'Two factors weigh heavily against the effectiveness of scientific research in industry.', 'http://static2.iyuba.cn/newconcept/british/4/4_15.mp3', '有两个因素严重地妨碍工业中科学研究的效率：', '19.8', 4015, 'effectiveness', 0, 0, 4, 3015, 3165);
