INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/50/75/5075140835d0bc504791c76b04c33d2b.mp3', 'n.过程', '83.4', '13', 5, 'ˈprəuses', 'Yet a great many processes depending on such research are sought for with complete secrecy until the stage at which patents can be taken out.', 'http://static2.iyuba.cn/newconcept/british/4/4_15.mp3', '然而，依赖这种研究的很多工艺程序是在完全保密的情况下进行的，直到可以取得专利权的阶段为止。', '73.7', 4015, 'process', 0, 0, 4, 3015, 3168);
