INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/9e/29/9e29c075ccc81e5c14ccbcb39ef9e91c.mp3', 'n.流行病', '70.0', '9', 14, 'ˌepiˈdemik', 'By infecting animals and letting them loose in the burrows, local epidemics of this disease could be created.', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '通过让染上此病的动物在洞内乱跑，就可以使这种疾病在一个地区蔓延起来。', '63.2', 4017, 'epidemic', 0, 0, 4, 3017, 3202);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/f0/fe/f0fee5650c92a2d28715070d15f88eb8.mp3', 'n.蚊虫', '79.4', '10', 15, 'məˈski:təu', 'Later it was found that there was a type of mosquito which acted as the carrier of this disease and passed it on to the rabbits.', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '后来又发现，有一种蚊子是传播这种疾病的媒介，能把此病传染给兔子。', '71.1', 4017, 'mosquito', 0, 0, 4, 3017, 3203);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/d4/5e/d45ec3c4af38967de454f9e45bb1cef4.mp3', 'n.带菌者', '79.4', '10', 16, 'ˈkæriə(r)', 'Later it was found that there was a type of mosquito which acted as the carrier of this disease and passed it on to the rabbits.', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '后来又发现，有一种蚊子是传播这种疾病的媒介，能把此病传染给兔子。', '71.1', 4017, 'carrier', 0, 0, 4, 3017, 3204);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/e1/b6/e1b6b3c0f7e578dbaf25572cedce4d63.mp3', 'v.消灭', '105.9', '14', 17, 'ɪkˈstɜ:mineɪt', 'so that the rabbit population was unlikely to be completely exterminated.', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '所以兔子不可能被完全消灭。', '101.2', 4017, 'exterminate', 0, 0, 4, 3017, 3205);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/c/3/5/c35fa38996edb33dae49d6012fd05fe0.mp3', 'ad.具有讽刺意味地', '121.8', '16', 18, 'ai''rɔnikli', 'Ironically, Europe, which had bequeathed the rabbit as a pest to Australia, acquired this man-made disease as a pestilence.', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '具有讽刺意味的是，欧洲把这种兔子作为有害动物传给澳洲，而欧洲自己却染上了这种人为的瘟疫般的疾病。', '113.2', 4017, 'ironically', 0, 0, 4, 3017, 3206);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/d7/62/d762b3251253eb9b2ef98932b913b5f1.mp3', 'v.把…传给', '121.8', '16', 19, 'bɪˈkwi:ð', 'Ironically, Europe, which had bequeathed the rabbit as a pest to Australia, acquired this man-made disease as a pestilence.', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '具有讽刺意味的是，欧洲把这种兔子作为有害动物传给澳洲，而欧洲自己却染上了这种人为的瘟疫般的疾病。', '113.2', 4017, 'bequeath', 0, 0, 4, 3017, 3207);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/fc/3a/fc3a4fb327706ca2220500871d60b94b.mp3', 'n.害虫，有害动物', '143.5', '19', 20, 'pest', 'It spread through France, where wild rabbits are not generally regarded as a pest but as a sport and a useful food supply,', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '结果在整个法国蔓延开来。野兔在法国一般不被当作有害动物，而被视为打猎取乐的玩物和有用的食物来源。', '134.7', 4017, 'pest', 0, 0, 4, 3017, 3208);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/84/fc/84fc8f68bdb379c38b44b1be721eb98b.mp3', 'n.瘟疫', '121.8', '16', 21, 'ˈpestɪləns', 'Ironically, Europe, which had bequeathed the rabbit as a pest to Australia, acquired this man-made disease as a pestilence.', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '具有讽刺意味的是，欧洲把这种兔子作为有害动物传给澳洲，而欧洲自己却染上了这种人为的瘟疫般的疾病。', '113.2', 4017, 'pestilence', 0, 0, 4, 3017, 3209);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/aa/1f/aa1f6bc154e04ddf5e7e93f747e6e0f6.mp3', 'n.范围', '133.9', '18', 22, 'kənˈfain', 'It did not, however, remain within the confines of this estate.', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '然而，这种疾病并未被局限在他的庄园内，', '130.2', 4017, 'confine', 0, 0, 4, 3017, 3210);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/30/28/3028b93ed138b6600da039b11297ac34.mp3', 'v.驯养', '150.5', '20', 23, 'dəˈmestɪkeɪt', 'and it spread to Britain where wild rabbits are regarded as a pest but where domesticated rabbits,', 'http://static2.iyuba.cn/newconcept/british/4/4_17.mp3', '这种疾病又蔓延到了英国。在英国，野兔被当作有害的动物，', '143.6', 4017, 'domesticate', 0, 0, 4, 3017, 3211);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c7/2e/c72e9bea2eeb1f1d389492f9cc29a891.mp3', 'n.海豚', '14.4', '3', 1, 'ˈpɔːpəs', 'What would you say is the main characteristic of porpoises?', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '你认为江豚的主要特征是什么?', '10.7', 4018, 'porpoise', 0, 0, 4, 3018, 3212);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/a9/09/a909b944804141354049983d9c6cc236.mp3', 'n.水手', '25.0', '4', 2, 'ˈmærinə(r)', 'There has long been a superstition among mariners that porpoises will save drowning men by pushing them to the surface,', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '长期以来，海员中流传着一种迷信的说法，认为海豚会把快要淹死的人托到水面，救人性命；', '16.9', 4018, 'mariner', 0, 0, 4, 3018, 3213);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/da/67/da6776e7ec7eaa7a6f3df5c6b149127e.mp3', 'n.鲨鱼', '30.2', '5', 3, 'ʃɑ:k', 'or protect them from sharks by surrounding them in defensive formation.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '或在人们周围列队保护，使他们免遭鲨鱼伤害。', '25.6', 4018, 'shark', 0, 0, 4, 3018, 3214);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/f1/a4/f1a41f239896dc63a6eaeba863f0e7ce.mp3', 'n.队形', '30.2', '5', 4, 'fɔ:ˈmeiʃn', 'or protect them from sharks by surrounding them in defensive formation.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '或在人们周围列队保护，使他们免遭鲨鱼伤害。', '25.6', 4018, 'formation', 0, 0, 4, 3018, 3215);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/6f/d4/6fd4287a8ac77fe65e0f9be274942642.mp3', 'n.海豚科动物', '41.5', '7', 5, '‘dɔlfin', 'it is probably a mistake to credit dolphins with any motive of life-saving.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '认为它们有救人的动机可能是错误的。', '36.8', 4018, 'dolphin', 0, 0, 4, 3018, 3216);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/93/24/932414080c583ee6ed861b3a62dba2b3.mp3', 'a.不省人事的', '53.0', '8', 6, 'ʌn''kɔnʃəs', 'On the occasions when they have pushed to shore an unconscious human being they have much more likely done it out of curiosity or for sport,', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '当它们偶尔把一个失去知觉的人推到岸边时，更大的可能是出于好奇或游戏，', '43.4', 4018, 'unconscious', 0, 0, 4, 3018, 3217);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/3e/ba/3eba9a0d2274b11693fd821353a6149f.mp3', 'n.海狸', '65.2', '10', 7, 'ˈbi:və(r)', 'In 1928 some porpoises were photographed working like beavers to push ashore a waterlogged mattress.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '1928年，有人拍摄到了海豚像海狸一样把浸透水的床垫推上岸的情景。', '57.6', 4018, 'beaver', 0, 0, 4, 3018, 3218);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/77/2a/772a0f87cf8e179e8c372883a65daf10.mp3', 'ad.上岸', '65.2', '10', 8, 'ə''ʃɔ:(r)', 'In 1928 some porpoises were photographed working like beavers to push ashore a waterlogged mattress.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '1928年，有人拍摄到了海豚像海狸一样把浸透水的床垫推上岸的情景。', '57.6', 4018, 'ashore', 0, 0, 4, 3018, 3219);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/f5/7b/f57b3f2b73ce9a134da29aa0f6a04f15.mp3', 'a.浸满水的', '65.2', '10', 9, 'ˈwɔ:təlɔgd', 'In 1928 some porpoises were photographed working like beavers to push ashore a waterlogged mattress.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '1928年，有人拍摄到了海豚像海狸一样把浸透水的床垫推上岸的情景。', '57.6', 4018, 'waterlogged', 0, 0, 4, 3018, 3220);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/8a/91/8a9191c5a32c460444a5148f4f0bf37f.mp3', 'n.香味', '78.1', '12', 10, 'sent', 'it may have been because curiosity attracted them and because the scent of a possible meal attracted the sharks.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '那么它们可能是出于好奇；而鲨鱼可能是闻到了可以美食一顿的香味。', '71.0', 4018, 'scent', 0, 0, 4, 3018, 3221);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/8c/6f/8c6fd8ffb88cc57527d2bef98627a08a.mp3', 'v.接着发生', '89.9', '14', 11, 'en''sju:', 'It is possible that upon such an occasion a battle ensued, with the sharks being driven away or killed.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '双方可能随之发生搏斗，搏斗结果是海豚赶走或咬死鲨鱼。', '82.3', 4018, 'ensue', 0, 0, 4, 3018, 3222);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/dc/dc/dcdc968437484c9b283214bf67602c70.mp3', 'v.引起兴趣', '97.2', '15', 12, 'inˈtri:ɡ', 'Whether it be bird, fish or beast, the porpoise is intrigued with anything that is alive.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '海豚对凡是活的东西都感兴趣，不管是鸟、是鱼，还是野兽。', '91.1', 4018, 'intrigue', 0, 0, 4, 3018, 3223);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/66/a5/66a571e7115b29030455d691b726916d.mp3', 'n.侮辱', '103.7', '16', 13, 'in''dignəti', 'They are constantly after the turtles, who peacefully submit to all sorts of indignities.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '它们经常追逐海龟，海龟则温顺地忍受着各种侮辱。', '97.9', 4018, 'indignity', 0, 0, 4, 3018, 3224);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/5d/54/5d54cc37750da53911560fe8bf292320.mp3', 'n.口鼻部', '109.8', '17', 14, 'snaut', 'One young calf especially enjoyed raising a turtle to the surface with his snout,', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '一只小海豚特别喜欢用鼻子把海龟推到水面，', '104.9', 4018, 'snout', 0, 0, 4, 3018, 3225);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/50/82/50820fc2dab363a675d125730bc58260.mp3', 'v.硬推', '114.3', '18', 15, 'ʃʌv', 'and then shoving him across the tank like an aquaplane.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '然后像滑水板一样把海龟从水池的这一边推到那一边。', '110.0', 4018, 'shove', 0, 0, 4, 3018, 3226);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/d0/ef/d0ef0719faa8e4d14ed4df042a4010f0.mp3', 'n.驾浪滑水板', '114.3', '18', 16, 'ˈækwəpleɪn', 'and then shoving him across the tank like an aquaplane.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '然后像滑水板一样把海龟从水池的这一边推到那一边。', '110.0', 4018, 'aquaplane', 0, 0, 4, 3018, 3227);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/64/01/6401ee8f258a6daec377731fe275992f.mp3', 'n.水族馆', '141.7', '21', 17, 'ˌəʊʃəˈneəriəm', 'In another game, as the turtle swims across the oceanarium, the first porpoise swoops down from above and butts his shell with his belly.', 'http://static2.iyuba.cn/newconcept/british/4/4_18.mp3', '在另一场游戏中，当海龟游过水族馆时，第一只海豚从上方猛扑下去，用腹部撞击龟壳。', '132.1', 4018, 'oceanarium', 0, 0, 4, 3018, 3228);
