INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/a2/b6/a2b6f2a6066ed8700d83335fc50a2b8e.mp3', 'a.奇特的', '47.6', '6', 3, 'ɔd', 'and one odd finding that makes the problem puzzling is that it looks very much as if sleeping is not simply a matter of giving the body a rest.', 'http://static2.iyuba.cn/newconcept/british/4/4_19.mp3', '一项使人对这个问题感到困惑的奇怪的发现是，睡眠在很大程度似乎并不仅仅是为了使身体得到休息。', '38.7', 4019, 'odd', 0, 0, 4, 3019, 3236);
