INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/cd/69/cd69fc4e49f9d63d4bee27226aaa9b27.mp3', 'n.精神病学家', '127.0', '16', 10, 'sai''kaiətrist', 'Some years ago an American psychiatrist named William Dement published experiments dealing with the recording of eye-movements during sleep.', 'http://static2.iyuba.cn/newconcept/british/4/4_19.mp3', '前些年，美国一位精神病学者发表了一篇报告，报告中记录了眼球在睡眠时的活动情况。', '117.3', 4019, 'psychiatrist', 0, 0, 4, 3019, 3243);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/1e/5f/1e5fab21d63b18d996e5aa1ab8851d41.mp3', 'v.不时介入', '139.3', '17', 11, '''pʌŋktʃʊeɪt', 'He showed that the average individual''s sleep cycle is punctuated with peculiar bursts of eye-movements, some drifting and slow, others jerky and rapid.', 'http://static2.iyuba.cn/newconcept/british/4/4_19.mp3', '他指出，平常人的睡眠周期中不时伴有一阵阵奇怪的眼球队活动，这些活动有的飘忽而缓慢，有的急剧而快速。', '128.4', 4019, 'punctuate', 0, 0, 4, 3019, 3244);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/29/c0/29c007fea08b936277a23b6702fb04aa.mp3', 'a.急动的', '139.3', '17', 12, '’dʒɜ:kɪ', 'He showed that the average individual''s sleep cycle is punctuated with peculiar bursts of eye-movements, some drifting and slow, others jerky and rapid.', 'http://static2.iyuba.cn/newconcept/british/4/4_19.mp3', '他指出，平常人的睡眠周期中不时伴有一阵阵奇怪的眼球队活动，这些活动有的飘忽而缓慢，有的急剧而快速。', '128.4', 4019, 'jerky', 0, 0, 4, 3019, 3245);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/63/b1/63b1d95acc054476b1547a6901e84c84.mp3', 'n.失调', '170.5', '22', 13, 'disˈɔ:də(r)', 'the first group began to show some personality disorders while the others seemed more or less unaffected.', 'http://static2.iyuba.cn/newconcept/british/4/4_19.mp3', '结果，第一组人开始出现性格失常，而第二组人似乎没受什么影响。', '164.2', 4019, 'disorder', 0, 0, 4, 3019, 3246);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/3d/2f/3d2fe6855a56ee8a53a1672481c64592.mp3', 'n.表明', '178.1', '23', 14, 'ˌɪmpliˈkeiʃn', 'The implications of all this were that it was not the disturbance of sleep that mattered, but the disturbance of dreaming.', 'http://static2.iyuba.cn/newconcept/british/4/4_19.mp3', '这一切暗示我们：睡眠受到干忧没关系，而做梦受到干忧是有问题的。', '171.4', 4019, 'implication', 0, 0, 4, 3019, 3247);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/1e/89/1e893922a5ddc59deda87b8f38f360cb.mp3', 'n.唾液', '31.3', '5', 1, 'səˈlaɪvə', 'Over the periods their saliva, a mild, digestive juice like our own, was converted into a poison that defies analysis even today.', 'http://static2.iyuba.cn/newconcept/british/4/4_20.mp3', '蛇的唾液本来和我们人的消化液一样柔和，但经过漫长的时间，演变成了今天仍无法分析清楚的毒液。', '21.9', 4020, 'saliva', 0, 0, 4, 3020, 3248);
