INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/00/df/00dfd219ddd96a46d8447d8cb6067635.mp3', 'n.被捕食的动物', '43.6', '7', 5, 'prei', 'they could have caught and lived on prey without using poison, just as the thousands of non-poisonous snakes still do.', 'http://static2.iyuba.cn/newconcept/british/4/4_20.mp3', '它们也可以不用毒液捕捉动物而生存，就像今天成千上万的无毒蛇那样。', '36.3', 4020, 'prey', 0, 0, 4, 3020, 3252);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/b2/14/b214af95fab423cc843792c70a199f09.mp3', 'a.凶猛的', '68.5', '12', 6, 'fiəs', 'no running fights with large, fierce rats or tussles with grown rabbits--just a bite and no more effort needed.', 'http://static2.iyuba.cn/newconcept/british/4/4_20.mp3', '它就不必再和又大又凶的老鼠边跑边博斗了，也不必再和大兔子扭斗了，只需要咬一口就可以了。', '60.8', 4020, 'fierce', 0, 0, 4, 3020, 3253);
