INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/52/61/5261539cab7de0487b6b41415acc7f61.mp3', 'n.(活动的)中心', '28.0', '4', 1, 'hʌb', 'The Moon is likely to become the industrial hub of the Solar System supplying the rocket fuels for its ships,', 'http://static2.iyuba.cn/newconcept/british/4/4_35.mp3', '月球很可能成为太阳系的工业中心。从月球上的岩石中可以获得航天飞船的燃料，', '19.7', 4035, 'hub', 0, 0, 4, 3035, 3501);
