INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/31/f2/31f2385ba9cc65dba7ccb9aa5c5b7600.mp3', 'n.阿波罗', '78.8', '13', 4, 'əˈpɔləʊ', 'Because the gravity on the Moon''s surface is only a sixth of Earth''s (remember how easily the Apollo astronauts bounded along),', 'http://static2.iyuba.cn/newconcept/british/4/4_35.mp3', '由于月球表面的重力仅是地球表面的1/6 -- 还记得阿波罗飞船中的宇航员累松地跳跃 --', '70.0', 4035, 'Apollo', 0, 0, 4, 3035, 3504);
