INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/08/c5/08c54608ef5311d325ced2e7eab2f0b9.mp3', 'n.魅力', '138.1', '22', 8, 'ˌfæsəˈneɪʃn', 'Mars has an extraordinary fascination for would-be voyagers.', 'http://static2.iyuba.cn/newconcept/british/4/4_35.mp3', '火星对未来的星际旅客说有着特殊的魅力。', '134.1', 4035, 'fascination', 0, 0, 4, 3035, 3508);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c1/a1/c1a1738648ecda410dc3a0dbbb3be683.mp3', 'a.资历深的，年长的', '149.7', '23', 9, 'ˈsi:njə(r)', 'America, Russia and Europe are filled with enthusiasts--many of them serious and senior scientists who dream of sending people to it.', 'http://static2.iyuba.cn/newconcept/british/4/4_35.mp3', '美国、俄罗斯和欧洲都有许多热心此项事业的人 -- 其中的不少是认真和资深的科学家，他们一直梦想着把人送上火星。', '139.0', 4035, 'senior', 0, 0, 4, 3035, 3509);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/48/77/4877b2c41a66e95f897cfb2eef0cf965.mp3', 'n.断层，裂口', '175.7', '27', 10, 'ˈkæzəm', 'cloudless skies, savage sandstorms, chasms wider than the Grand Canyon and at least one mountain more than twice as tall as Everest.', 'http://static2.iyuba.cn/newconcept/british/4/4_35.mp3', '无云的天空，凶猛的沙暴，比大峡谷还宽的裂缝，起码有一座山有珠穆朗玛峰的近两倍高。', '165.0', 4035, 'chasm', 0, 0, 4, 3035, 3510);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/c3/0a/c30ac88409cb015104dd8c547d059d59.mp3', 'n.峡谷', '175.7', '27', 11, 'ˈkænjən', 'cloudless skies, savage sandstorms, chasms wider than the Grand Canyon and at least one mountain more than twice as tall as Everest.', 'http://static2.iyuba.cn/newconcept/british/4/4_35.mp3', '无云的天空，凶猛的沙暴，比大峡谷还宽的裂缝，起码有一座山有珠穆朗玛峰的近两倍高。', '165.0', 4035, 'canyon', 0, 0, 4, 3035, 3511);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/1/b/e/1be02a44eda5fed5e7d0ca1684e3962e.mp3', 'a.分裂的', '26.3', '4', 1, 'ˌdɪsju:''naɪtɪd', 'If a nation is essentialy disunited, it is left to the government to hold it together.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '如果一个国家实际上处于分裂状态，使之联合起来就是政府的事了。', '21.2', 4036, 'disunited', 0, 0, 4, 3036, 3512);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/5/b/d/5bd432e9ac4030e5aa4087f717f366d1.mp3', 'ad.相应地', '37.0', '5', 2, ',kɔrə''spɑndɪŋli', 'This increases the expense of government, and reduces correspondingly the amount of economic resources that could be used for developing the country.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '这样的一来就增加了政府的开支，从而相应地减少了可以用来了展国家的那部分经济资源。', '27.6', 4036, 'correspondingly', 0, 0, 4, 3036, 3513);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/19/5f/195fe59b6f103787a914aead0f3db502.mp3', 'a.落后的', '43.8', '6', 3, 'ˈbækwəd', 'And it should not be forgotten how small those resources are in a poor and backward country.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '不应忘记，在一个贫穷落后的国家里，那部分财力是很有限的。', '38.2', 4036, 'backward', 0, 0, 4, 3036, 3514);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/90/18/901887859d1fa1e436631eddf37739be.mp3', 'v.承担', '64.9', '9', 4, 'ɪnˈkɜː(r)', 'An enterprise has to incur certain costs and expenses in order to stay in business.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '一个企业为了继续经营，不得不支出一定的费用和开销。', '59.3', 4036, 'incur', 0, 0, 4, 3036, 3515);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/08/f2/08f2ece20ba70e227a951fc20cea1204.mp3', 'v.管理', '74.6', '10', 5, 'əd''minəstə(r)', 'For our purposes, we are concerned only with one kind of cost--the cost of managing and administering the business.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '就我们的目的而言，我们只关心一种费用 -- 企业行政管理费。', '65.9', 4036, 'administer', 0, 0, 4, 3036, 3516);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/14/70/1470a5fa592a8766b352f4aba57ea10d.mp3', 'a.行政管理的', '82.1', '11', 6, 'əd''minəstreitiv', 'Such administrative overheads in a business are analogous to the cost of government in a nation.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '一家企业的行政管理开支类似于一个国家的政府管理所用的开支。', '75.8', 4036, 'administrative', 0, 0, 4, 3036, 3517);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/54/0c/540cf52bcdee1ac30e37508315766ac0.mp3', 'a.类似的', '82.1', '11', 7, 'əˈnæləgəs', 'Such administrative overheads in a business are analogous to the cost of government in a nation.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '一家企业的行政管理开支类似于一个国家的政府管理所用的开支。', '75.8', 4036, 'analogous', 0, 0, 4, 3036, 3518);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/b3/99/b399ce0b04ee639fe3b7fd3f86f40032.mp3', 'n.一般费用', '89.4', '12', 8, 'ˈəʊvəhedz', 'The administrative overheads of a business are low to the extent that everyone working in the business', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '企业的管理费用就会降低到相应的程度，如果企业中的每个人', '83.1', 4036, 'overheads', 0, 0, 4, 3036, 3519);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/fe/f4/fef418a4cdacbfc5397b9ee51c0c2765.mp3', 'n.主动，积极性', '106.9', '15', 9, 'iˈniʃiətiv', 'and to exercise such initiative as falls within their sphere, then administrative overheads will be low.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '在各自的工作范围内发挥主动性，行政管理费用就会降低。', '99.3', 4036, 'initiative', 0, 0, 4, 3036, 3520);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/76/98/7698d507735e3a82f9d7fdc8a7545f3a.mp3', 'n.检查人员', '138.5', '19', 10, '''tʃekə(r)', 'then the business will require armies of administrators, checkers and foremen and administrative overheads will rise correspondingly.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '那公企业就会需大批的管理人员、检查人员和带班人员，管理费用就会相应在增加。', '129.1', 4036, 'checker', 0, 0, 4, 3036, 3521);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/a0/cc/a0cc2b322ea1b3becaa224bf4041a43e.mp3', 'n.监工', '138.5', '19', 11, 'ˈfɔ:mən', 'then the business will require armies of administrators, checkers and foremen and administrative overheads will rise correspondingly.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '那公企业就会需大批的管理人员、检查人员和带班人员，管理费用就会相应在增加。', '129.1', 4036, 'foreman', 0, 0, 4, 3036, 3522);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/6d/f4/6df497b4bf909db09f1a36aef2b8d526.mp3', 'n.红利', '156.1', '21', 12, 'ˈdividend', 'and the business will have less money to distribute as dividends or invest directly in its future progress and development.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '因此用于分红的金额就用于将来开拓和发展的投资就相应地减少了。', '148.7', 4036, 'dividend', 0, 0, 4, 3036, 3523);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/55/66/55668f78b8cf0bed357d578828a41ad9.mp3', 'ad.过度地', '194.0', '27', 13, 'ʌn''dju:li', 'A disunited nation therefore has to incur unduly high costs of government.', 'http://static2.iyuba.cn/newconcept/british/4/4_36.mp3', '因此，一个处于分裂的国家必须要支付过高的行政管理费用。', '186.3', 4036, 'unduly', 0, 0, 4, 3036, 3524);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/8b/ff/8bff5c0ea5d278fd384a55ecb7590339.mp3', 'n.可能性', '38.5', '5', 1, '''laiklihud', 'It has yet to reach its full size and strength, and its owner his or her full intelligence: but at this age the likelihood of death is least.', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '虽然这个时期人的身材、体力和智力还有待发展和完善，但在这个年龄死亡的可能性最小。', '27.5', 4037, 'likelihood', 0, 0, 4, 3037, 3525);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/bd/21/bd21b9319bb3f43da3c1fdf54000d637.mp3', 'n.婴儿', '44.9', '6', 2, 'ˈinfənt', 'Earlier, we were infants and young children, and consequently more vulnerable;', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '再早一些，我们是幼儿和小孩子，身体较脆弱；', '39.4', 4037, 'infant', 0, 0, 4, 3037, 3526);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/25/89/25890deab1075e916c06b9e1efc2e25f.mp3', 'a.脆弱的', '44.9', '6', 3, 'ˈvʌlnərəbl', 'Earlier, we were infants and young children, and consequently more vulnerable;', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '再早一些，我们是幼儿和小孩子，身体较脆弱；', '39.4', 4037, 'vulnerable', 0, 0, 4, 3037, 3527);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/50/14/5014b4aadb30b7363e53abfc7b9212b1.mp3', 'a.感觉不到的', '53.2', '7', 4, 'ˌɪmpəˈseptəbl', 'later, we shall undergo a progressive loss of our vigour and resistance which, though imperceptible at first', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '再迟一些，我们就要经历生命力和抵抗力逐步衰退的过程。', '46.0', 4037, 'imperceptible', 0, 0, 4, 3037, 3528);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/00/bd/00bd624b5b21d2b07edf398c1ce98b5e.mp3', 'a.强健的', '114.1', '16', 5, 'rəuˈbʌst', 'and there is a virtual limit on how long we can hope to remain alive, however lucky and robust we are.', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '不管我们多么幸运，多么健壮，我们所希望的长寿实际上是有限度的。', '107.4', 4037, 'robust', 0, 0, 4, 3037, 3529);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/82/a6/82a6e4584f7ce6534aa3422e1e50bcc7.mp3', 'n.有机体', '151.2', '22', 6, 'ˈɔ:ɡənizəm', 'and probably other organisms such as trees, or even the universe itself, must in the nature of things ''wear out''.', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '大概也认识到所有的有机物。如树木，甚至宇宙本身，从事物的本质上来说都会“磨损掉”。', '142.8', 4037, 'organism', 0, 0, 4, 3037, 3530);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/1f/94/1f944f065283ad028fa7fdb7057092d2.mp3', 'n.热力学', '173.5', '26', 7, 'ˌθɜːməʊdaɪˈnæmɪks', 'do in fact run out of energy in accordance with the second law of thermodynamics (whether the whole universe does so is a moot point at present).', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '也都会消耗完其能量（整个宇宙否如此，目前沿有争论）。', '163.8', 4037, 'thermodynamics', 0, 0, 4, 3037, 3531);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/35/2b/352be54f4e66a44d2fc1b235cf18170a.mp3', 'a.急转直下的', '57.3', '8', 8, 'sti:p', 'will finally become so steep that we can live no longer,', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '虽然这个过程起初难以觉察，', '53.6', 4037, 'steep', 0, 0, 4, 3037, 3532);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/f2/33/f233513e32f3431f7d5e0e1864427eb5.mp3', 'n.老化', '5.1', '1', 9, 'ˈeɪdʒɪŋ', 'Lesson 37 The process of ageing', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '课文37  衰老过程', '1.6', 4037, 'ageing', 0, 0, 4, 3037, 3533);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/2f/29/2f297b731b78157674e81f0d561c20c0.mp3', 'n.可能性', '97.3', '13', 10, 'ɔdz', 'so that there are heavy odds in favour of our dying between the ages of 65 and 80.', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '我们最可能死亡的年龄在65至80岁之间，', '90.8', 4037, 'odds', 0, 0, 4, 3037, 3534);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/31/4e/314e639555fe0f158539c7e04e7ddad6.mp3', 'a.实际上的', '114.1', '16', 11, '''və:tʃuəl', 'and there is a virtual limit on how long we can hope to remain alive, however lucky and robust we are.', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '不管我们多么幸运，多么健壮，我们所希望的长寿实际上是有限度的。', '107.4', 4037, 'virtual', 0, 0, 4, 3037, 3535);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/9b/d9/9bd989a05beb236438224f20df5674a3.mp3', 'a.争论未决的', '173.5', '26', 12, 'mu:t', 'do in fact run out of energy in accordance with the second law of thermodynamics (whether the whole universe does so is a moot point at present).', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '也都会消耗完其能量（整个宇宙否如此，目前沿有争论）。', '163.8', 4037, 'moot', 0, 0, 4, 3037, 3536);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/d/1/a/d1ac70b829a2ea04af3187a695c71c10.mp3', 'a.破旧的', '183.3', '28', 13, 'rʌn daʊn', 'A run-down watch is still a watch and can be rewound.', 'http://static2.iyuba.cn/newconcept/british/4/4_37.mp3', '手表停了依然是只手表，还可以重上好发条。', '179.6', 4037, 'run-down', 0, 0, 4, 3037, 3537);
