INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/d3/d3/d3d35b2365bdc9dba2422c53930e6b30.mp3', 'n.灵感', '46.4', '7', 2, 'ˌɪnspəˈreiʃn', 'they are in that condition of eager discomfort which passes for inspiration all admit radical changes of destination once the journey has begun;', 'http://static2.iyuba.cn/newconcept/british/4/4_39.mp3', '他们处于急切不安的状态，而被当作是灵感。他们无不承认，一旦“旅程”开始，“目的地”常有急剧的变化。', '36.0', 4039, 'inspiration', 0, 0, 4, 3039, 3554);
