INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/37/e7/37e7cd4e480e4c2985b0b7458c0d0dad.mp3', 'a.无法描述的', '86.7', '13', 7, 'ˌɪndɪˈskraɪbəbl', 'This organic process, often leading to moments of extraordinary self-discovery, is of an indescribable fascination.', 'http://static2.iyuba.cn/newconcept/british/4/4_39.mp3', '这种有机的加工过程往往达到不寻常自我发现的境界，具有难以言表的构思魅力。', '77.6', 4039, 'indescribable', 0, 0, 4, 3039, 3559);
