INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/24/e2/24e264be42bf74c78eebff5014849724.mp3', 'v.怨恨', '260.2', '40', 25, 'ɡrʌdʒ', 'Each day is a holiday, and ordinary holidays, when they come, are grudged as enforced interruptions in an absorbing vocation.', 'http://static2.iyuba.cn/newconcept/british/4/4_46.mp3', '每天都是假日，而通常的假期来到，他们却惋惜这假期强制打断了他们埋头从事的工作。', '251.3', 4046, 'grudge', 0, 0, 4, 3046, 3700);
