INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/7d/05/7d05c898c4f470761a3c54fff87a29f5.mp3', 'v.排除，放弃', '280.7', '42', 27, 'ˈbæniʃ', 'Indeed, it may well be that those whose work is their pleasure are those who most need the means of banishing it at intervals from their minds.', 'http://static2.iyuba.cn/newconcept/british/4/4_46.mp3', '说实在的，把工作当作享受的那些人最需要每隔一段时间把工作从头脑中撇开。', '270.9', 4046, 'banish', 0, 0, 4, 3046, 3702);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/ae/f1/aef1d70c2ca770a6ff8ce06622e6d720.mp3', 'n.假定', '40.4', '6', 1, 'ə''sʌmpʃn', 'But, contrary to a popular assumption, it is far from being the only one, or even the greatest.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '但是，和一般的看法相反，这决非是仅有的，甚至不是最主要的动机。', '34.0', 4047, 'assumption', 0, 0, 4, 3047, 3703);
