INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/51/18/5118394f28be1c11f7322ab20540cf0a.mp3', 'n.自相矛盾的事', '67.5', '9', 4, 'ˈpærədɔks', 'That the equipment of modern camping becomes yearly more sophisticated is an entertaining paradox for the cynic,', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '现代露营装备一年比一年讲究，这对那些厌世嫉俗者来说是一件有趣的自相矛盾的事情。', '60.2', 4047, 'paradox', 0, 0, 4, 3047, 3706);
