INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/02/28/0228aba94293984a373586af55dc0f2b.mp3', 'n.社会学家', '83.6', '11', 6, 'ˌsəuʃi''ɔlədʒist', 'It also provides--and some student sociologist might care to base his thesis upon the phenomenon--an escape of another kind.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '学社会学的大学生来露营是另一种形式的摆脱现实，他们的目的很可能是根据观察到的露营现象去写论文。', '73.9', 4047, 'sociologist', 0, 0, 4, 3047, 3708);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/12/55/125565a5ed9dcc02a5ce69f12a07a533.mp3', 'v.避开', '97.2', '13', 7, 'ʃʌn', 'not because he cannot afford, or shuns their material comforts, but because he is afraid of them.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '这并不是因为他们付不起钱，也不是为了躲避物质享受，而是因为他们害怕酒店。', '90.8', 4047, 'shun', 0, 0, 4, 3047, 3709);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/a1/8b/a18b8d81620f23289dab8524992149b5.mp3', 'a.富有的', '104.8', '14', 8, '''æfluənt', 'Affluent he may be, but he is by no means sure what to tip the doorman or the chambermaid.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '他们可能很富有，但给看门人和房间女服务叫多少小费，心中却根本没有数；', '98.0', 4047, 'affluent', 0, 0, 4, 3047, 3710);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/e6/f1/e6f19ce9027d78f0095b85e3146b211a.mp3', 'n.女招待员', '104.8', '14', 9, 'ˈtʃeɪmbəmeɪd', 'Affluent he may be, but he is by no means sure what to tip the doorman or the chambermaid.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '他们可能很富有，但给看门人和房间女服务叫多少小费，心中却根本没有数；', '98.0', 4047, 'chambermaid', 0, 0, 4, 3047, 3711);
