INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/5e/68/5e6893907778fafaa5afc6958bc79a00.mp3', 'n.势利', '122.3', '17', 11, 'ˈsnɒbəri', 'Granted, a snobbery of camping itself, based upon equipment and techniques, already exists;', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '诚然，露营地本身也存在以露营装备和方式取人的势利现象，', '115.7', 4047, 'snobbery', 0, 0, 4, 3047, 3713);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/b8/1c/b81ca7c0ccaa77e7aa91936ab0070695.mp3', 'n.等级制度', '136.6', '19', 12, 'ˈhaiərɑ:ki', 'There is no superior ''they'' in the shape of managements and hotel hierarchies to darken his holiday days.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '但在露营地里根本不会有管人的“人上人”和酒店里的等级制度来种露营者的假日过得阴郁低沉。', '128.9', 4047, 'hierarchy', 0, 0, 4, 3047, 3714);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/oxford/0/fa/12/fa1271330a7f744e6747cff7e65f94d6.mp3', 'v.使成为必要', '151.0', '21', 13, 'in''teil', 'The contemporary phenomenon of car worship is to be explained not least by the sense of independence and freedom that ownership entails.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '当前崇拜汽车现象可以用与所有权相伴的独立和自由意识来解释。', '141.9', 4047, 'entail', 0, 0, 4, 3047, 3715);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/1b/13/1b131c7d26a51195412c438860adbceb.mp3', 'a.险恶的', '177.1', '25', 14, 'ɪnˈklemənt', 'but the means of escape from holiday hell (if the beach proves too crowded, the local weather too inclement)', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '而且也是逃离假日地狱（如海滩太挤，当地天气恶劣）方便工具，', '170.6', 4047, 'inclement', 0, 0, 4, 3047, 3716);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/ed/d2/edd211850974c992279fa461de57c196.mp3', 'n.由旅行社安排一切的一揽子旅游', '188.5', '27', 15, 'ˈpækɪdʒ tʊə', 'Idealists have objected to the practice of camping, as to the package tour,', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '理想主义者像反对旅行社安排一切的一揽子旅游一样反对露营的作法，', '183.2', 4047, 'package tour', 0, 0, 4, 3047, 3717);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res-tts.iciba.com/8/a/5/8a5892bc56fcf0f934219a4f95c8a193.mp3', 'n.偏狭', '201.4', '29', 16, 'ˈɪnsjʊˈlærətɪ', 'Insularity and self-containment, it is argued, go hand in hand.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '他们争论说，心胸狭窄和自我封闭是并存的。', '196.6', 4047, 'insularity', 0, 0, 4, 3047, 3718);
INSERT OR REPLACE INTO WordItem (audio, def, end_timing, examples, position, pron, sentence, sentence_audio, sentence_cn, timing, voa_id, word, correct, userId, bookId, unitId, id) VALUES ('http://res.iciba.com/resource/amp3/0/0/54/b0/54b0e7bf11cb04424cded2d1818b4ac6.mp3', 'v.迎合', '215.4', '31', 17, 'ˈkeitə(r)', 'Holiday hotels tend to cater for one nationality of visitors especially, sometimes exclusively.', 'http://static2.iyuba.cn/newconcept/british/4/4_47.mp3', '假日旅馆有只接待来自一个国家的旅游者的倾向，有时会达到排他的程度。', '208.6', 4047, 'cater', 0, 0, 4, 3047, 3719);
